manifest.MakePostfix:
function (docid) {
	var that = this;
	that.db.load(docid, true)
	.then(function(doc){
		if ( doc && doc._attachments ) that.Postfix.files = doc._attachments;
	})
}

manifest.MakeSubs:
function (formid) {
	var that = this,
			i, 
			childs = that.db.ram(function(e){
				return (e.type == "manifest" && e.manifest && e.manifest.id.startsWith(formid+"."))
			});

	if (childs.length) {
		childs = childs.map(function(e){return Object.clone(e,true)});
		childs.forEach(function(e){
			var man = cw.lib.unjson(e.manifest),
					ref = man.id.from(formid.length+1);
			$.extend(!0, that.Subs, cw.lib.unmask(man, ref));
			man = cw.lib.getref(that.Subs, ref);

			//has attaches?
			if (Object.size(e._attachments)) {
				that.db.load(e._id, true)
				.then(function(man2){
					man.files = man2._attachments;
				});
			}

		});
	}
}

manifest.CMoverlays:
function () {

	// Extends CM with DOM id highlighter
	// and supplementary highlighting for color picker

	var re_clr = /^(rgb[a]?\s*\([\s0-9,\.]+\)|#[0-9a-fA-F]{6}|#[0-9a-fA-F]{3})/,
			re_domid = /^('#[a-z0-9\-_]{1,20}'|'\.[a-z0-9\-_\.]{1,20}'|'input\[[^\]]{1,20}\]'|"#[a-z0-9\-_]{1,20}"|"\.[a-z0-9\-_\.]{1,20}"|"input\[[^\]]{1,20}\]")/i,
			re_domid_x = /^('#[a-f0-9]{6}'|"#[a-f0-9]{6}")/i

	function _parseStream (stream, state) {
		var ch, n1, n2;

		if (stream.match(re_clr)){
			stream.eat(re_clr);
			return "csscolor";
		}
		else if (stream.match(re_domid) && !stream.match(re_domid_x)){
			stream.eat(re_domid);
			return "domidcolor";
		}
		while (
			stream.next() != null 
			&& !stream.match(re_clr, false)
			&& !stream.match(re_domid, false)
		) {}
		return null;
	}

	// - - - - - - - - - - - - -

	[
		["manjs", "javascript"],
		["manhtml", "htmlmixed"],
		["mancss", "text/css"]
	].forEach(function(e){
		if (
			//true || 
			!CodeMirror.modes[e[0]]
		) CodeMirror.defineMode(e[0], function(config, parserConfig) {
			return CodeMirror.overlayMode(CodeMirror.getMode(config,e[1]), {token: _parseStream});
		});
	})

}

manifest.InitCM:
function () {

	// inits different CM stuff

	var that = this;

	if (!CodeMirror.overlayMode) _ovl(CodeMirror);

	that.CMoverlays();

	return ;


	// - - - - - - - - - -

	function _ovl(CodeMirror) {
		"use strict";

		CodeMirror.overlayMode = function(base, overlay, combine) {
			return {
				startState: function() {
					return {
						base: CodeMirror.startState(base),
						overlay: CodeMirror.startState(overlay),
						basePos: 0,
						baseCur: null,
						overlayPos: 0,
						overlayCur: null,
						streamSeen: null
					};
				},
				copyState: function(state) {
					return {
						base: CodeMirror.copyState(base, state.base),
						overlay: CodeMirror.copyState(overlay, state.overlay),
						basePos: state.basePos,
						baseCur: null,
						overlayPos: state.overlayPos,
						overlayCur: null
					};
				},

				token: function(stream, state) {
					if (stream != state.streamSeen ||
							Math.min(state.basePos, state.overlayPos) < stream.start) {
						state.streamSeen = stream;
						state.basePos = state.overlayPos = stream.start;
					}

					if (stream.start == state.basePos) {
						state.baseCur = base.token(stream, state.base);
						state.basePos = stream.pos;
					}
					if (stream.start == state.overlayPos) {
						stream.pos = stream.start;
						state.overlayCur = overlay.token(stream, state.overlay);
						state.overlayPos = stream.pos;
					}
					stream.pos = Math.min(state.basePos, state.overlayPos);

					// state.overlay.combineTokens always takes precedence over combine,
					// unless set to null
					if (state.overlayCur == null) return state.baseCur;
					else if (state.baseCur != null &&
									 state.overlay.combineTokens ||
									 combine && state.overlay.combineTokens == null)
						return state.baseCur + " " + state.overlayCur;
					else return state.overlayCur;
				},

				indent: base.indent && function(state, textAfter) {
					return base.indent(state.base, textAfter);
				},
				electricChars: base.electricChars,

				innerMode: function(state) {
					return {
						state: state.base,
						mode: base
					};
				},

				blankLine: function(state) {
					if (base.blankLine) base.blankLine(state.base);
					if (overlay.blankLine) overlay.blankLine(state.overlay);
				}
			};
		};

	}
}

manifest.SaveDoc:
function () {
  var that = this,
      d = that.data,
      pi = $.Deferred(),
      dbid = this.db.name; 

  d.editor.stamp=Date.now();

  if (d.editor.manifest) {
    var build = +d.editor.raw.build || (d.editor._rev?+d.editor._rev.split("-")[0]:null) || 1;
    if (!Object.isNumber(build)) build = 1;
    d.editor.manifest.build = build + 1;
    d.editor.raw.build = build + 1;
    if (d.editor.manifest.app && d.editor.manifest.app.hasOwnProperty('build')) {
    	d.editor.manifest.app.build = build + 1;
    }
  }

  that.db.save(d.editor)
  .then(function (res){

    // mark manifest as trusted
    if (window.cw && window.cw.read) {
      cw.read[dbid].trust[res._id] = res._rev;
      cw.read[dbid]._dirty = true;
    }

    //push to app
    Object.merge(
      d.editor,
      Object.select(res,[/^_/, "stamp", "log"])
    );

    //redraw file list
    that.my.find('#atts').trigger("redraw");

    //rebuild 
    that.MakePostfix(d.editor._id);

    pi.resolve(d.editor);

  })
  .fail(function(err, res){
    console.log(res, err);
    pi.reject(err,res);
  });

  return pi.promise();
}

