E.TabCollapse:
function (reflist) {

    // Re-builds collapse list for tabs

    var that = this,
        curr = that.Collapsed,
        coll = curr.reduce(function(a,b,i){a[b[0]] = i;return a;},{}),
        res = [],
        c;

    // Need optimization!

    c= (reflist || that.data.com.map(function(e){return e.id}))
    .sortBy()
    .reduce(function(a,b){
      var ins = -1;
      if(!a.a[b]) {
        var k = Object.keys(a.a);
        k.forEach(function(e,i){
          if (ins==-1 && b.startsWith(e+".")) ins=i;
        });
      }
      if (ins>-1) {
        a.coll[k[ins]] = (a.coll[k[ins]] || 0)+1;
      }
      else a.a[b]=0;
      return a;
    },{a:{},coll:{}})["coll"];

    Object.keys(c)
    .forEach(function(k){
      if (k in coll) res.push([k, curr[coll[k]][1], c[k]]);
      else res.push([k, false, c[k]]);
    });

    that.data.collapsed = res.map(function(e){
      if (e[1]) return e[0];
    }).compact();

    return that.Collapsed = res;

  }

E.Build:
function (buildPreview) {
    var that=this,
        d = that.data,
        opts = that.Types, 
        err=[], r={}, p={}, 
        row, ext, conv, a;
    if (Object.isFunction(that.Prefix)) $.extend(true, r, that.Prefix());

    if (buildPreview) $.extend(true, p, r);

    for (var i=0;i<d.com.length;i++) {
      row = d.com[i];
      ext = {};

      conv = opts[row.type].compile || function(s){return [[], s||""];};
      a = conv(row.data);

      if (a[0].length) err.push("Invalid ‘"+row.id+"’. "+a[0].join("; ")+".");
      if (a[1]!==undefined) {
        if (row.id.to(1)===".") {
          ext = cw.lib.unmask(a[1], row.id.from(1));
        } else {
          ext = a[1];
        }
      }

      if (buildPreview) $.extend(true, p, ext);
      if (!row.opts || row.opts.indexOf("nocompile")===-1) $.extend(true, r, ext);
    }

    if (Object.isFunction(that.Postfix)) {
      var tmp = that.Postfix()||{};
      if (buildPreview) $.extend(true, p, tmp);
      $.extend(true, r, tmp);
    }

    d._err = err;

    //that._src = "("+this.js2txt(r,'\t')+")";

    that._src = JSON.parse(cw.lib.json(r));

    if (buildPreview) that._preview = "("+this.js2txt(p,'\t')+")";

    r = p = ext = null;

    return err;
  }

E.ParseJS:
function (s,clean) {

    var g=s,
        u = UglifyJS, 
        ast,
        res ={
          globals:[],
          errors:[]
        },
        gl;
    try{
      ast = u.parse(s+"");
    } catch (e) {
      res.errors.push (e.message+" @"+e.line+":"+e.col)
    }
    if (ast) {
      ast.figure_out_scope();
      if (Object.isObject(gl = ast.globals) && gl._size) {
        Object.keys(gl._values)
        .forEach(function(i){
          res.globals.push(gl._values[i].name);
        });
        res.globals.sort();
        if (clean) {
          res.globals = [].remove.apply(
            res.globals,
            ("String Function Date Math Number Object Array RegExp isNaN undefined parseInt parseFloat "
             +"isFinite").split(" ")
          );
        }
      }
    }
    return res;
  }

E.init:
function ($form) {	
	var that = this,
      d = that.data;

  //$form.formgen('init',this.HTML);	

	this.js2txt = cw.lib.js2txt;

	$.extend (this.Ed, {NewTab: Object.clone(this.NewTab, true), Types: this.Types});

	$.extend(this.Tab, {Types: this.Types});

	if (this.Mode.saveToLs) {
		// Saved?
		var ls = localStorage, old = ls.getItem("Manifesto");
		if (old) Object.merge(d, JSON.parse(old));
		// localStorage saver
		$form.then(function(){
			$form.on("change", function (){
				ls.setItem("Manifesto", JSON.stringify($form.my("data")));
			}.debounce(500));
		});
	}

	if (d._preview!==void 0) delete d._preview;

	if (!d.com) d.com = [];
	d.com = d.com.map(function(e){
		if (!Object.isObject(e) || !e.id || !("data" in e)) return null;
		if (!that.Types[e.type]) e.type="text";
		return e;
	}).compact(true);
	if (!d.com.length) {
		d.com.push(Object.clone(this.Item, !0));
		d.com[0].stamp = Date.now()+"";
		d.com[0].data = that.NewAppSrc;
	}

	d._src="";

	var act = d.com.find(function(e){return e.active}) || d.com[0];
	act.active = true;
	this.Editors.push(act);	

	// Remove tab

	$form.on("removetab.my", function(evt, data){
		var t = d.com, e = that.Editors;
		t.removeAt(t.indexOf(data));
		e.removeAt(e.indexOf(data));
		t[0].active=true;

		that.TabCollapse();
		$form.find("#coms,#eds").trigger("recalc");
	});

	// Tab Collapser
	if (d.collapsed) that.Collapsed = d.collapsed.map(function(e){return [e, true,1]});
	that.TabCollapse();

}

