    }

#eds.init:
function ($o) {
      // Color Picker magic
      if ($.my.cache("cw.Sys.Colorpicker")) {
        $o.on("contextmenu.my",".cm-csscolor", function(evt){
          var $i = $(evt.currentTarget),//$(this),
              c = $i[0].innerText+"",
              cm = $i.parents("section").find("#cm").next()[0].CodeMirror,
              x = evt.pageX,
              y = evt.pageY,
              up = $(window).height()/2>y;

          $i.modal({
            width:272,
            css:"p0",
            manifest:$.my.cache("cw.Sys.Colorpicker"),
            data:{css:c},
            align:up?"top:100%":"bottom:140%",
            nose:up?"top":"bottom",
            screen:"rgba(255,255,255,0.5)",
            esc:true,
            enter:true,
            close:false,
            focus:"#cprgb"
          })
          .then(function(picker){
            //console.log(res);
            var co = cm.coordsChar({left:x, top:y}),
                line = co.line,
                idx = co.ch,
                cd = cm.getDoc(),
                s = cd.getLine(line),
                sl = s.length,
                res = s;
            for (var i=idx; i>-1; i--) {
              var s0 = s.substr(i);
              if (s0.startsWith(c)) {
                res = s.to(i) + s0.replace(c, picker.css.toLowerCase());
                break;
              }
            }
            cd.replaceRange(res, {line:line, ch:0}, {line:line, ch:sl})
            //$i.text(picker.css).trigger("input");
          });
          return false;
        });
      }
    }

