css.compile:
function (css) {
			var err=[], res="";
			res = ((css+"")
			.replace(/\/\*[\s\S]+?\*\//gm,"")
			.replace(/@charset[^;]+?;/gim,'')
			.replace(/[\n\t\s]+/g,' ')
			.replace(/\}/g,'}⊻')
			.replace(/^\n+/g,"").replace(/[\n\s]+$/g,"")
			.split('⊻')
			.compact(true)
			.reduce(function(a,b){
				var t = b.trim(), p, k, v;
				if (/^[^{]+\{[^\}]+\}$/.test(t)) {
					p = t.to(-1).split("{");
					k = " "+p[0].trim();
					v = p[1].trim();
					if (v.last()!=";") v+=";";
					if (!a[k]) a[k]="";
					a[k]+=v;
				} else {
					err.push("Invalid rule "+t.truncate(40));
				}
				return a;
			},{}));
			return [err, res];
		}

