        $o.on("contextmenu.my",".cm-csscolor", function(evt){
          var $i = $(evt.currentTarget),//$(this),
              c = $i[0].innerText+"",
              cm = $i.parents("section").find("#cm").next()[0].CodeMirror,
              x = evt.pageX,
              y = evt.pageY,
              up = $(window).height()/2>y;

          $i.modal({
            width:272,
            css:"p0",
            manifest:$.my.cache("cw.Sys.Colorpicker"),
            data:{css:c},
