({
  '#tab-search':{
    delay:150,
    bind:function(d,v){
      var that = this;
      if (v!=null) {
      	d._search = v;
      }
      if (d._search.length < 2) that.searchRe = null;
      else that.searchRe = new RegExp('('+RegExp.escape(d._search)+')', 'i');
      return d._search;
    },
    css: { 	"my-search": /^.{2,}$/ }
  },

  '#btn-run':{
    delay:150,
    bind:function (d,v,$o){
      var $r, $e, $p, dui, ref, doc, parentapp, _prefix, that=this, runnow=true;
      if (v!=null) {
        that.Build (true);
        if (d._err.length) cw.lib.note(d._err.join("<br>"),"error");
        else {
          $r = $o.my().root;
          if ($r) dui = $r.my().dui;

          if (dui) {
            ref = dui.bind.split(".").to(-1).join(".");
            doc = $.my.f.getref($r.my().ddata, ref);
            if (doc && Object.isObject(doc._attachments) && Object.size(doc._attachments)) {
              // try read attaches
              parentapp = $r.my().root.data("app");
              if (parentapp && parentapp.db && parentapp.db.load) {
                runnow = false;
                parentapp.db.load(doc._id, true)
                .then(function(res){
                  if (res._attachments) _prefix = {files:res._attachments};
                  _runPreview();
                })
              } 
            }	
          } 
          if (runnow) _runPreview();
        }
      }

      function _runPreview(){ 
        // Preview in modal
        if (d._opts.runMode=="modal") {
          var $r = $o.parents("#manedframe").eq(0);
          $r.modal({
            root:$r.parent(),
            manifest: that.Preview,
            data:{
              _opts:d._opts,
              _src:that._preview,
              _prefix:_prefix,
              _subs:that.Subs,
              _db:that.db
            },
            width:d._opts.runWidth||1000,
            esc:true,
            global:false,
            //screen:'rgba(255,255,255,0.5)',
            drag:true
          }).then(function(r){
            //Object.merge(d._opts,r,true)
          })
          /*.fail(function(res){
					cw.lib.note("Preview failed to init. "
						+"See console for details", "error");
				});*/
        }

        // Preview fullscreen
        else if (d._opts.runMode=="fullscreen") {
          $p = $(that.Mode.previewParent||"body");

          $p.find(">*:visible").each(function(){
            var $t = $(this);
            if (
              !$t.is(".my-modal")
              && !$t.is(".my-modal-screen")
              && !$t.is(".my-modal-proxy")
            ) $t.addClass("hide my-manifesto-preview-hide");
          });		

          $e = $(
            '<div class="my-manifesto-preview-run db" '
            +'style="'+that.Mode.fullscreenStyle
            +';"></div>'
          );

          $e.appendTo($p)
          .my(that.Preview, {
            _opts:d._opts,
            _src:that._preview,
            _prefix:_prefix,
            _subs:that.Subs,
            _db:that.db
          }).fail(function(){
            cw.lib.note("Preview failed to init. "
                        +"See console for details", "error");
            _kill();
          }.debounce(50));
          $e.on("cancel.my", _kill.debounce(50));

        }
      }

      function _kill (){
        try {
          $e.my("remove");
        } catch (e) {}
        $e.unbind(".my");
        $e.remove();
        $p.find(".my-manifesto-preview-hide")
        .removeClass("hide my-manifesto-preview-hide");
      }
    }
  }
})

