    red:["#A3293D", "rgba(255, 200, 200, 0.75)"],
    green:["rgb(75, 129, 1)","#E3F3CB"],
    teal:["#07AB8F", "rgba(198, 252, 234, 0.67)"],
    orange:["#F77400","rgb(255, 227, 185)"],
    violet:["rgba(109, 37, 179, 0.9);", "rgba(210, 142, 255, 0.25);"]
  },
  ui:{
    "#xtitle":{
      bind: function(d,v,$o){
        var that = this,
            Coll=false,
            clr = d.color?d.color+"":"",
            colors = that.Colors;
        if (clr) {
          $o.css({ color:colors[clr][0] });
          if (d.active) { $o.css({ "background-color":colors[clr][1] }); } 
          else { 	$o.css({ "background-color":"" }); }
        }
        else $o.css({ color:"", "background-color":"" });

        $o.css({opacity:d.active?"1":"0.7"});
      },
      css:{ 
        "active": function(d){ return d.active },
        "bold fs105" : function (d){ return (d.id||"").to(1)!=="."},
        "itl": function (d){ return (d.opts||[]).indexOf("nocompile")>-1},
        "haserror": function(d) {return d.err && d.err.length}
      },
      watch:"#title"
    },
    ".fold":{
      //delay:200,
      bind:function(d,v,$o){
        var that = this, 
            $p, re, found = true,
            html="";

        if (v==null) {
          $p =that.my.root();
          re = that.getSearchRe();

          if (re) {
            if (!re.test(d.id) && !re.test(d.data)) {
              found = false;
              html = '';
            }
            $p.toggleClass("w00", !found);
          }
          else {
						found = false;
            that.getCollapsed()
            .forEach(function(e){
              if (d.id==e[0]) {
                html = e[1] ? '<span class="fs120">▸</span>'+e[2] : '<span class="fs120">▾</span>'+e[2];
              }
              else if (d.id.startsWith(e[0]+".")) {
                var folded = !!$p.is(".w00");
                $p.toggleClass("w00", !!e[1] && !d.active);
                found = true;
                if (folded) {
                  $p.css({"background-color":"rgba(255,240,0,0.5)"});
                  (function(){$p.css({"background-color":"rgba(255,255,255,0)"});}).delay(150);
                }
              }
            });
            if (!found) $p.removeClass("w00");
          }
          return html;
        }
        else return v;
      }
    },
    "#title":{
      bind:function(d,v,$o){
        var that = this;
        if (null != v) {
          if (!d.active) $o.trigger("switch", d);
          else if (d.err && d.err.length) {
            var e = that.ParseJS("("+d.data+")");
            d.err = e.errors;
            $o.modal({
              root:$o.my().root.parents(".my-form").eq(0),
              width:250,
              nose:"top",
              bound:0,
              align:"top:120%;",
              esc:true,
              enter:true,
              manifest:{
                init:['<span id="t" class="red o80 fs85 lh150"></span><input type="text" class="di fs50 o0 w00 mt0 mb0"/>'],
                ui:{"#t":"t","input":"x"}
              },
              data:{t:e.errors.join("<br>")},
              screen:'rgba(255,255,255,0.5)'
            })
          }
        }
        var s = (d.id == 'Root'?that.RT('data').editor.raw.title:'') || d.id,
            sl = s.replace(/[^\.]/g,'').length;
        if (sl>1) {
          var hasParent = (that.getCollapsed() || []).find(function(e){
          	return d.id.startsWith(e[0]+".");
          });
          if (hasParent) s = ('\u00a0').repeat(sl*2-2)+s;
        }
        s+='\u00a0';
        return s;
      },
      events:"click.my",
      init: function($o, form){
        var d = form.data;
        $o.parent().parent().find(".ctype")
        .addClass(this.Types[d.type].css||"hide");
      }
    }
  }
})

