({
	init:function ($o){ 
		$o.formgen(this.HTML); 
	},
	About:{},
	Stats:0,
	ui:{
		"#opts":{
			bind:function(d,v,$o){
				var that = this,
						jsmin = cw.lib.jsmin, 
						toj = $.my.tojson;
				if (v!=null) {
					var i = cw.lib.a2o(v),
							//_src = d._src;
							_src;

					if (i.childs) {
						//_src = "("+cw.lib.js2txt($.extend(!0, eval(d._src), d.Subs()),'\t')+")";
						_src = "("+cw.lib.js2txt($.extend(!0, cw.lib.unjson(d._src), d.Subs?d.Subs():{}),'\t')+")";
					} 
					else {
						_src = "("+cw.lib.js2txt(cw.lib.unjson(d._src),'\t')+")";
					}
					//else _src = d._src;

					if (i.json) d.src = toj(eval(_src), '\t');
					else if (i.cjson) d.src = toj(eval(jsmin(_src)));
					else if (i.jsmin) d.src = jsmin(_src);
					else if (i.uglify) {
						// compress and uglify 
						var u = UglifyJS,
								ast = u.parse(_src);
						ast.figure_out_scope();
						ast = ast.transform(u.Compressor({side_effects: false}));
						ast.figure_out_scope();
						// No char freq opt to avoid 
						// different results from the same code
						//ast.compute_char_frequency();
						ast.mangle_names();
						d.src = ast.print_to_string().replace(/<\/script>/ig,'<\\u002fscript>').replace(/;$/,'');
					}
					else d.src=_src;

					this.Stats = unescape(encodeURIComponent(d.src)).length;
					this.About = this.ParseJS("("+(i.json||i.cjson?_src:d.src) +")", true);

					d.opts = v;
					if (!d.opts.length || d.opts+""=="childs") d.opts.push("js");
				}
				return d.opts;
			},
