({
	"html":{
		CM: {mode:'manhtml', theme:"base16-light"},
		css:"fi-html5",
		compile: null
	},
	//---------------------  TEXT  -------------------
	"text":{
		CM:{ mode:""},
		compile: null
	},
	//---------------------  JS  -------------------
	"js":{
		CM:{ mode:'manjs'},
		compile: function(s){
			var err=[], res = null;
			try {
				// Eval is unsafe and resource-consuming
				//res = eval(s);

				// More safe way
				res = (new Function ("", "return ("+s+");"))();

			} catch (e) {
				err.push(e.message+".");
			}
			return err.length?[err, undefined]:[err,res];
		}
	},
	//---------------------  CSS  -------------------
	"css":{
		CM:{ mode:"mancss", theme:"base16-light"},
		css:"fi-css3",
		compile: function(css){
			var err=[], res="";
			res = ((css+"")
			.replace(/\/\*[\s\S]+?\*\//gm,"")
			.replace(/@charset[^;]+?;/gim,'')
			.replace(/[\n\t\s]+/g,' ')
			.replace(/\}/g,'}⊻')
			.replace(/^\n+/g,"").replace(/[\n\s]+$/g,"")
			.split('⊻')
			.compact(true)
			.reduce(function(a,b){
				var t = b.trim(), p, k, v;
				if (/^[^{]+\{[^\}]+\}$/.test(t)) {
					p = t.to(-1).split("{");
					k = " "+p[0].trim();
					v = p[1].trim();
					if (v.last()!=";") v+=";";
					if (!a[k]) a[k]="";
					a[k]+=v;
				} else {
					err.push("Invalid rule "+t.truncate(40));
				}
				return a;
			},{}));
			return [err, res];
		}
	}
})

