manifest.init:
function ($o,form) {

		var that = this,
				lang = 'en';

		// Locale
		//if (cw.me && that.Lang[cw.me('locale')]) lang = cw.me('locale');
		//that.Lang = that.Lang[lang];
    that.Lang = that.lang;

		// editor component container
		$o.formgen(that.HTML);

		// Parser
		if (!window.HTMLParser) that.HTMLParser(window);

		// Collapse content
		form.data.doc.content = that.CollapseItems(form.data.doc.content);

		// DB
		that.Ed.db = that.db;

		// Cropper btns
		that.Ed.Cropper.style[' .tag-in-fmt'] = Object.clone(that.style[' .tag-in-fmt'],true);

		// Items
		//that.Editor.Items = $.extend(true, {}, that.T);
		that.Ed.Items = that.T;
		that.Ed.Items.img.Opts.style[' .tag-in-fmt'] =
		that.Ed.Items.embed.Opts.style[' .tag-in-fmt'] = that.style[' .tag-in-fmt'];

		//enc key
		form.data.key= form.data.doc.crypto || "_";
	}

manifest.die:
function ($o) {
		// Free resources 
		var u = this.SessionUrls||{};
		Object.keys(u).forEach(function(i){  URL.revokeObjectURL(u[i]); });

		// Unmount niceScroll if any
		if (!/Firefox/.test(navigator.userAgent)) $o.find('#meditor').getNiceScroll().remove();
	}

#meditor.init:
function ($o) {
			var b = navigator.userAgent;
			if (/Mac.+Firefox/.test(b)) $o.css({overflow:'scroll'});
			else if (/iPad.+Safari/.test(b)) $o.css({overflow:"scroll",'-webkit-overflow-scrolling': 'touch'});
			else if (/Mac.+Chrome\//.test(b)) $o.css({overflow:'overlay'});
			else if (/Mac.+Safari/.test(b)) $o.css({overflow:'overlay'});
			else if (!/Firefox/.test(b)) $o.niceScroll({zindex:7});
		}

#doctags.bind:
