				that.my.restyle();
			}
			return d.doc.tags;
		}

#encrypt.bind:
function (d,v,$o) {
			if (v!=null) {
				d.key=v;
				if (v!="_") d.doc.crypto=v;
				else delete d.doc.crypto;
