#btn-addphoto.bind:
function (d,v,$o) {
			var that = this;
			if (v!=null) {
				that.my.modal('#btn-addphoto',{
					width:821,
					global:true,
					screen:true,
					esc:true,
					manifest:$.extend(true, {Att:that.Att}, that.Ed.Cropper, {data:{gallery:false}}),
					data:{}
				})
				.then(function(res){
					if (!d.doc._attachments) d.doc._attachments = {};
					var atts = d.doc._attachments,
							fname = res.filename;

					atts[fname] = {
						content_type:'image/jpeg',
						data:res.data
					};

					// gen preview

					var $i = $('<img src=""/>');
					$i.attr("src", "data:image/jpeg;base64,"+res.data);
					$i.load(function(){
						var ico = cw.lib.image($i,"white").resample(150),
