#btn-undo.bind:
function (d0,v,$o) {
			var that = this,
					d = d0.doc, 
					cth = {}, 
					ctchanged = false,
					stylechange = false;

			if (v!=null) {

				var h = that.my.find('#meditor').my().params.history  || {},
						hd0 = cw.lib.crc2(d),
						keys = Object.keys(h).sortBy(),
						ct,
						obj;

				if (keys.length) {
					try {
						if (keys.length > 1 ) {
							while (hd0 == cw.lib.crc2(h[keys.last()])) {
								delete h[keys.last()];
								keys.pop();
							}
						}
						obj = h[keys.last()];
						var ct = $.extend(true,[], obj.content),
								atts = $.extend(true,{}, obj._attachments||{}),
								style = $.extend(true,{}, obj.style||{}),
								previews = $.extend(true,{}, obj.previews||{});
						if (keys.length > 1 ) {
							delete h[keys.last()];
							keys.pop();
						}
					} catch(e) {
						console.log(e);
					}
				}

				if (ct) {

					// Content, only changes

					d.content.forEach(function(e, i){
						var hash = cw.lib.crc2(Object.reject(e, ['random']));
						cth[e._stamp] = {hash:hash, idx:i}
					});
					d.content = ct.map(function(e){
						var old, hash;
						if (old = cth[e._stamp]) {
							hash = cw.lib.crc2(Object.reject(e, ['random']));
							if (old.hash == hash) return d.content[old.idx];
						}
						ctchanged = true;
						e.random = Number.random(1e6,1e7)+""; 
						return e;
					});

					// Styles
					if (cw.lib.crc2(style) != cw.lib.crc2(d.style)) {
						d.style = style;
						stylechange = true;
					}

					// Attaches

					if (Object.size(atts)) {
						d._attachments = atts;
						d.previews = previews;
					} 
					else {
						d.previews = {};
						delete d._attachments;
					}

					that.my.recalc("#atts");
					if (ctchanged || stylechange) that.my.recalc("#medium");
					if (stylechange) that.my.restyle();

				} else {
					cw.lib.note(that.Lang.MSG_NO_UNDO)
				}
			}
			return that.Lang.BTN_UNDO;
		}

#atts.init:
function ($o,form) {
			var that = this,
					d = form.data.doc;
			$o.my("cw.Sys.Attachments", {
				doc:d,
				truncate:20,
				previews:'previews',
				sort: function (attObj, fname){
					//sort by preview / name
					if ((that.data.doc.previews||{})[fname]) {
						return "01-"+fname;
					}
					var ext = fname;
					// filter hidden
					return /^\./.test(fname)?null:"02-"+ext;
				},
				ico:function (fobj, fname){
					if (d.previews[fname]) {
						return [
							'<div class="img-preview30" ',
							'style="background-image:url('+d.previews[fname].data+');"></div>'
						].join('');
					}
					var css="fi-paperclip",
							types={
								"fi-photo":/\.(png|jp[e]?g|gif|tif[f]?|bmp)$/i,
								"fi-social-adobe":/\.(pdf|ai|psd)$/i,
								"fi-page-doc":/\.(doc[x]?|txt|rtf)$/i,
								"fi-graph-pie":/\.(xls[x]?|csv)$/i,
								"fi-archive":/\.(zip|rar|7z|7zip)$/i,
								"fi-html5 pl2":/\.(htm[l]|php)$/i,
								"fi-css3 pl2":/\.css$/i,
								"fi-widget":/\.(js[on]?|php|asp|jar|c|erl)$/i,
								"fi-sound":/\.(wav|aiff|mp3|ogg|mpeg3)$/i
							};
					if (!fobj.digest) css="orange fi-burst-new";
					else Object.each(types, function(key, re){
						if (re.test(fname)) css=key;
					});
					return '<span class="'+css+' dib vam o80 lh110" style="width:1.3em;"></span>';
				},
				namePopup:true,
				upload:'<div class="hoverlink gray">'
					+'<span class="fi-paperclip fs105 mr2"></span> '
					+'<span class="pseudolink">'+that.Lang.UPLOAD+'</span>'
					+'</div>',
				noDelete: function(fname, doc){
					// this fn hides Del button for attaches in use
					var no = false,
							preview = doc.meta.preview || '';
					if (fname == preview) return true;
					doc.content.forEach(function(e){
						if (e.type=='img' && e.ref == fname) no = true;
						else if (e.type=='widget' && e.bg == fname) no = true;
						else if (e.type=='widget') {
							var it;
							try {it=JSON.parse(e.data.replace(/^\(|\)$/g,''))} catch(err){}
							if (Object.isObject(it)) {
								if (e.source == "Gallery" || e.source == "Files") {
									var j = it.items;
									if (Object.isArray(j) && j.length) {
										j.forEach(function(row){ if (row.url == fname) no = true; });
									}
								} // end gallery & files
								else if (e.source == "Cards") {
									var j = it.items;
									if (Object.isArray(j) && j.length) {
										j.forEach(function(row){ if (row.photo == fname) no = true; });
									}
								} // end cards
							}
						} // end widget check
					});
					return no;
				}
			});
		}

