manifest.Att:
function (fname,rtype) {
	//return undefined;

	// Returns attach in appropriate format, defined by rtype,
	// "url" returns session or data URL,
	// "data" returns data URL.
	// If rtype is object {content_type:"mime/type", data:"b64"},
	// appends this object to doc attachments.

	// First arg can be obj {name:fname, type:type}

	var that = this,
			u = that.SessionUrls || (that.SessionUrls = {}),
			d = that.data.doc,
			a = d._attachments || (d._attachments = {}),
			pending = that.PendingUrls || (that.PendingUrls = {});

	// Store
	if (fname && Object.isObject(rtype)) {
		a[fname] = Object.select(rtype, ['content_type','data']);
		if (rtype.preview && d.previews) {
			d.previews[fname] = rtype.preview;
		}
		that.my.find("#atts").trigger("recalc");
		return a;
	}

	// Recalc atts
	if (fname === true) {
		that.my.find("#atts").trigger("recalc");
	}

	if (fname == null) return a;

	if (rtype == "session") return u;

	if (rtype=="url" && u[fname]) return u[fname];
	else {
		var pi = $.Deferred(),
				id = that.data.doc._id,
				db = that.db;

		if (!id) pi.reject();
		else {

			//-------------------

			if (rtype=="url") {

				if (a && a[fname] && a[fname].data && !a[fname].digest) {
					return 'data:'+a[fname].content_type+';base64,'+a[fname].data;
				}

				if (pending[fname]) {
					pi.resolve();
					return pending[fname];
				}

				else {
					pending[fname] = pi.promise();
					cw.db(that.db.name)
					.getAttachment(id, fname+'')
					.then (
						function(blob){
							var url = URL.createObjectURL(blob);
							u[fname] = url;
							pi.resolve(url);
							pending[fname] = null;
						},
						function(err){ 
							console.log(err);
							pi.reject(); 
							pending[fname] = null;
						}
					);
				}
			}

			//-------------------

			else if (rtype=="data") {

				if (a && a[fname] && a[fname].data) {
					pi.resolve (a[fname].data);
				}
				else {
					cw.db(that.db.name).getAttachment(id, fname+'')
					.then (
						function(blob){
							$.my.f.blob2base64(blob, function(data){ pi.resolve(data); });
						},
						function(){ pi.reject(); }
					);
				}
			}

			//-------------------

			else if (rtype == "preview") {
				var names = [],
						res = [];
				if (Object.isString(fname)) names.push(fname);
				else if (Object.isArray(fname)) names = fname;
				else if (Object.isRegExp(fname)) {
					names = Object.keys(a).filter(fname).sortBy();
				}
				if (!names.length) pi.resolve([]);
				else {
					var icos = d.previews || (d.previews = {}),
							subnames = Object.keys(icos).intersect(names);
					if (subnames.length == names.length) {
						// we have all previews
						names.forEach(function(i){
							res.push($.extend({fname:i}, icos[i]));
						});
						pi.resolve(res);
					} else {
						// we have to generate previews
						subnames.forEach(function(i){
							res.push($.extend({fname:i}, icos[i]));
						});
						var missing = names.subtract(subnames),
								pis = [];

						missing.forEach(function(i){
							var pi0 = $.Deferred();
							pis.push(pi0);
							if (a[i] && a[i].data) {
								_genPreview(i, a[i].content_type, a[i].data, pi0);
							}
							else {
								// load attach
								cw.db(that.db.name).getAttachment(id, i)
								.then (
									function(blob){
										$.my.f.blob2base64(blob, function(data){ 
											_genPreview(i, a[i].content_type, data, pi0);
										});
									},
									function(){ pi0.resolve(null); }
								);
							}
						}); // end forEach

						// now pis is list of promises
						$.when.apply(null, pis)
						.done(function(){
							for (var t, j=0; j<arguments.length;j++) {
								var t = arguments[j];
								if (t != null) icos[t.fname] = t;
							}
							missing.forEach(function(i){
								if (icos[i]) res.push(icos[i]);
							});
							res.sortBy('fname');
							pi.resolve(res);
						});

					}

				}
			}
		}

		return pi.promise();

		// - - - - - - - - - - - - - - - 

		function _genPreview(name,mime, data, pi0){
			var $i = $('<img src=""/>'),
					pv = {
						fname:name,
						size:'?',
						desc:'',
						dim:'?×?',
						data:''
					};
			$i.attr("src",'data:'+mime+';base64,'+data);
			$i.load(function(){
				var ico, 
						img = cw.lib.image($i,"white");
				pv.dim = img.width() + '×'+ img.height();
				var l = (data.length/4*3).round();
				pv.size = l.metric(l>1e6?2:0,false).replace(/([a-z])$/i,' $1')+"b";
				ico = img.resample(150);
				if (ico.height()>150) ico.resample(22500/ico.height());
				pv.data = ico.sharpen(0.2).contrast(1.05).jpeg(0.65);
				$i.remove();
				pi0.resolve(pv);
			});
		} 
	}

}

manifest.CleanHTML:
function (s) {

	var ctr = 0, res = _compress(s), res0;
	while (ctr < 6 && res0 != res) {
		res0 = res;
		res = _compress(res);
		ctr +=1
	}

	if (!res) res = '<p><br/></p>';

	return res;


	//- - - - - - - - - - -

	function _compact(s){
		return $('<span>'+s.replace(/[\s\n\r\t]+/g,' ')+'</span>').html()
		.replace(/(&nbsp;|\s)(—|–|-{1,2})(&nbsp;|\s)([а-яё])/ig,'\u00A0– $4')
		.replace(/(&nbsp;|\s)(—|–|-{1,2})(&nbsp;|\s)([\w])/ig,'\u202F—\u2006$4')
		.replace(/([\w])(&nbsp;|\s)(—|–|-{1,2})(&[a-z]+sp;|\s)+/ig,'$1\u202F—\u2006')
		.replace(/([\wа-яё0-9])'([\wа-яё0-9])/ig,'$1’$2')
		.replace(/(&nbsp;|\s)"([а-яё])/gi,' «$2').replace(/([а-яё])"(&nbsp;|\s)/gi,'$1» ')
		.replace(/(&nbsp;|\s)"(\w)/gi,' “$2').replace(/(\w)"(&nbsp;|\s)/gi,'$1” ');
	}

	// - - - - - - - - - - - - - - - - - 

	function _construct (tag, attrs, unary) {

		var i, c={id:null}, h = '<'+tag;

		if (/^(p|table|h\d|span|pre)$/.test(tag) && unary) return "";

		if (attrs && attrs.length) {
			for (i=0;i<attrs.length;i++) {
				var aname = attrs[i].name.toLowerCase(),
						aval = _attr (tag, aname, attrs[i].escaped);
				if (aval) {
					c[aname] = aname+(attrs[i].value!=null?'="'+aval+'"':'');
				}
			}
			Object.keys(c).forEach(function(e){ if (null!=c[e]) h+=" "+c[e]; });
		}
		h+=unary?'/>':'>';
		return h;
	}

	//- - - - - - - - - - -

	function _attr (tag, aname, v) {
		var i,j,a, s = v;

		if (aname == "style") {
			a = v.split(";").compact(true).map(function(e){return e.compact(true)});
			a = a.reduce(function(a,b) {
				if (/(inherit|initial)/i.test(b)) return a;
				if (/^(color|background-color):/i.test(b)) a.push(b);
				if (/^(td|table|th|img)$/.test(tag) && /^width:/.test(b)) a.push(b);
				if (/^(p|td|table|h\d|blockquote)$/.test(tag) && /^text-align:/.test(b)) a.push(b);
				return a;
			},[]);
			s = a.join("; ").compact(true);
		}
		else if (aname == "class") {
			// not all tags allowed to have class
			if (/^(i|b|em|strong|tr|tbody|li|ul|ol)$/.test(tag)) s="";
		}
		else if (aname == "src") {
			if (!/^(img)$/.test(tag)) s="";
		}
		else if (aname == "href" || aname == "target") {
			if (!/^(a)$/.test(tag)) s="";
		}
		else if (aname == "rowspan" || aname == "colspan") {
			if (!/^(td|th)$/.test(tag)) s="";
		}
		else if (aname == 'start') {
			if (!/^(ol)$/.test(tag)) s="";
		}
		else {
			if (!/^(id|name)$/i.test(aname)) s='';
		}

		return s;
	}

	//- - - - - - - - - - -

	function _compress (h){

		var parse = window.HTMLParser,
				keepSpace = [],
				stack=[],
				res=[];

		parse(h, {
			start: function(tag, attrs, unary) {
