						t = (stack.length || /^(p|h1|h2|h3|h4|table|blockquote|ul|ol|img|pre|hr)$/.test(rt))?rt:'p',
						html = _construct(t, rt!=t?[]:attrs, unary),
						espan = html == '<span>';
				if(/^(p|blockquote|h\d|pre)$/.test(t) && stack.length && stack.last().tag == t) {
					stack.last().closed = true;
					res.push('</'+t+'>');
				}
				res.push(espan?'':html);
				if (!unary) {
					if (/^(script|pre|textarea|code)$/.test(t)) {
						keepSpace.push(true);
					}
					stack.push({tag:rt, attrs:attrs, closed:!html, retag:t!=rt?t:null, empty:true, espan:espan});
				} else {
					stack.forEach(function(elt){elt.empty = false});
				}
			},
			end: function(tag) {
				var a, s, process, type, src,
						t = tag.toLowerCase();

				if (/^(script|pre|textarea|code)$/.test(t)) keepSpace.pop();

				var last = stack.pop();
				if (!last.closed) {
					if (last.empty && !last.espan) res.pop();
					else if (last.espan) res.push('');
					else res.push('</'+(last.retag || t)+'>');
				}

			},
			chars: function(chars) {

				if (!stack.length) res.push('<p>');

				if (keepSpace.length) res.push (chars);
				else res.push (_compact(chars));

				if (!stack.length) res.push('</p>');
				else {
					stack.forEach(function(elt){elt.empty = false});
				}
			},
			doctype: function(t) {res.push(t);},
		});

		//return res.join("") .replace(/<p><\/p>/g,'') .replace(/<p><br[\/]?><\/p>/g,'');

		return $('<div>'+res.join("") .replace(/<p><\/p>/g,'') .replace(/<p><br[\/]?><\/p>/g,'') +'</div>').html();
	}



	//- - - - - - - - - - -

	function _compressJS (s0) {
		var s = s0;
		// compress and uglify 
		var u = UglifyJS,
				ast = u.parse(s+"");
		ast.figure_out_scope();
		ast = ast.transform(u.Compressor({side_effects: false}));
		ast.figure_out_scope();
		ast.mangle_names();
		return ast.print_to_string();
	}


}

