manifest.CollapseItems:
function (items) {
	// Collapses subsequent .type:"html
	// items

	var a =[];
	items.forEach(function(elt){
		if (!a.length) a.push(elt);
		else {
			if (
				elt.type == "html" 
				&& a.last().type=="html"
				&& (elt.css||[]).sortBy()+"" == (a.last().css||[]).sortBy()+""
			) {
				a.last().data += elt.data;
			} 
			else if (elt.type == "html") {
				if (elt.data && elt.data.compact()) a.push(elt);
			}
			else a.push(elt);
		}
	})
	return a;
}

