To.html:
function (item,options) {
	var that = this,
			d = item,
			h = '',
			id = Number.random(1e6, 1e7-1),
			opts = Object.merge({
				style:null,
				path:''
			}, Object.isObject(options)?options:{}),
			res = {
				err:[],
				html:'',
				style:'',
				comment:''
			};
	if (!Object.isObject(d) || d.type != that.data.type) {
		res.err.push('Error: item of ' + that.data.type + ' type expected.');
		return res;
	}

	if (!d.ref && !d.data) {
		res.err.push('Error: ' + that.data.type + ' has no data to render.');
		return res;
	}

	id = d._stamp || id;

	var css=['in-item', 'in--'+d.type];
	css.push ((that.Formats[d.format || 'left750'] || {css:''}).css);
	(d.css||[]).forEach(function(e){css.push("in-"+e.toLowerCase().replace(/[^a-z0-9]+/ig,'-'))});
	css.push(!(d.css||[]).length?"in-item-nocss":"in-item-hascss");

	var url='';

	if (d.ref) {

		if (/^http[s]?:/.test(d.ref) || d.ref.to(1)=='/') url = d.ref;
		else {
			if (opts.Urls) {
				url = opts.Urls[d.ref];
			} else {
				url = opts.path+d.ref;
			}
		}

	} else {
		url = "data:image/jpeg;base64,"+d.data;
	}

	h = [
		'<div id="in-' + id + '" class="'+css.join(' ')+'">',
			'<div class="in-ct">',
				'<div id="imageframe" class="in--img-frame">',
					'<img src="' + url +'" alt="'
					+(d.showdesc+''=='show'?(d.desc+'').stripTags().truncate(100).escapeHTML():'')
					+'">',
				'</div>',
				'<div class="in--x-fader"></div>',
				(d.showdesc+''=='show'?[
						'<div id="descframe" class="in--img-desc">',
						'<div class="redactor_box"><div class="redactor_editor">',
							(d.desc || '').removeTags('script embed iframe input textarea button'.split(' ')),
						'</div></div>',
					'</div>'
				].join(''):''),
			'</div>',
		'</div>'
	].join('');

	res.html = h;

	return res;
}

