#embed.bind:
function (d,v,$o) {
				var that = this;
				if (that.Crc == cw.lib.sdbm(d)) return undefined;
				else {
					that.Crc = cw.lib.sdbm(d);
					if (!d.data) {
						return '<div class="w100p bg-lblue tac p30 fs80 blue">'
							+'<span class="pseudolink" id="btn-embed-new">Insert embed...</span>'
							+'</div>';
					}
					// find post-processor
					var proc=false;
					that.Postinit.forEach(function(a){
						if (!proc && a[0].test(d.data)) {
							proc=true;
							var f = a[1].debounce(a[2]);
							try { f.call(that, d.data); } catch (e) {}
						}
					})

					return d.data;
				}
			}

