To.html:
function (item,options) {
	var that = this,
			d = item,
			h = '',
			id = Number.random(1e6, 1e7-1),
			embedh = '',
			scripts = [],
			opts = Object.merge({
				style:null,
				path:''
			}, Object.isObject(options)?options:{}), 
			res = {
				err:[],
				html:'',
				style:'',
				comment:'',
				scripts:[]
			};
	if (!Object.isObject(d) || d.type != that.data.type) {
		res.err.push('Error: item of ' + that.data.type + ' type expected.');
		return res;
	}

	if (!d.data) {
		res.err.push('Error: ' + that.data.type + ' has no data to render.');
		return res;
	}

	id = d._stamp || id;

	var css=['in-item', 'in--'+d.type];
	if (d.provider) css.push('in--embed-provider-'+d.provider);
	css.push ((that.Formats[d.format || 'left750'] || {css:''}).css);
	(d.css||[]).forEach(function(e){css.push("in-"+e.toLowerCase().replace(/[^a-z0-9]+/ig,'-'))});
	css.push(!(d.css||[]).length?"in-item-nocss":"in-item-hascss");

	// Youtube
	if (d.provider == 'youtube' && /^https:\/\/www.youtube.com\/watch/.test(d.source) && d.css.indexOf("Autostart") > -1) {
		var embedid = d.source.split('?')[1].split('&').find(/^v=/).split("=")[1];
		scripts.push([
			'$(function(){var $item=$("#in-'+id+' #embed>div");',
			'var opts = '+JSON.stringify(d.css)+', html='+JSON.stringify(d.data)+',videoid="'+embedid+'";',
			_youtube.toString(),
			'_youtube();',
			'});'
		].join('\n'));
		embedh='<div></div>';
	}
	// Fallback
	else {
		embedh = d.data;
	}

	h = [
		'<div id="in-' + id + '" class="'+css.join(' ')+'">',
			'<div class="in-ct">',
				'<div id="embed" class="in--embed-frame">'+embedh+'</div>',
				'<div class="in--x-fader"></div>',
				(d.showdesc+''=='show'?[
					'<div class="in--embed-desc">',
						'<div class="redactor_box"><div class="redactor_editor">',
							(d.desc || '').removeTags('script embed iframe input textarea button'.split(' ')),
						'</div></div>',
					'</div>'
				].join(''):''),
			'</div>',
		'</div>'
	].join('');

	res.html = h;
	res.scripts = scripts;

	return res;

	// - - - - - - - - - - - - - 

	function _youtube(){
		var hasYT = ("YT" in window),
				okYT = hasYT && window.YT.Player,
				start = ((opts||[]).indexOf("Autostart") > -1),
				mute = ((opts||[]).indexOf("Mute") > -1),
				loop = ((opts||[]).indexOf("Loop") > -1),
				player;

		if (!hasYT || !start) $item.parent().html(html);
		else {
			if (okYT) {
				player = new YT.Player($item[0], {
					playerVars: {
						'autoplay': start?1:0,
						'controls': 0,
						'autohide': 1,
						'wmode': 'opaque',
						'showinfo': 0,
						'loop': loop?1:0,
						'mute': mute?1:0,
						'playlist': videoid
					},
					videoId: videoid,
					events: {
						//'onReady': onPlayerReady
					}
				});
			} 
			else {
				// Ping YT every 100ms
				setTimeout(_youtube, 100);
			}
		}

	}








