				else {
						if ( !isO(o.ui)) err.push("No ui section");
						else if ( !Object.size(o.ui)) err.push("No bindings in ui section");

						if  ( !Object.isFunction(o.init) && !Object.isArray(o.init) && !(d.html+"").length)
							err.push("No HTML defined in init function or HTML tab");

						if (!isO(data))  err.push("Defaults must be an object");

						if (o.data!==undefined && !isO(o.data)) err.push("Section ‘data’ must be an object");
						if (o.params!==undefined && !isO(o.params)) err.push("Section ‘params’ must be an object");
						if (o.style!==undefined && !isO(o.style)) err.push("Section ‘style’ must be an object");
					}
				if (!err.length) d.valid = true;
				d.err=err;
			}

