To.html:
function (item,options) {
	var that = this,
			d = item,
			h = '',
			id = Number.random(1e6, 1e7-1),
			opts = Object.merge({
				style:null,
				path:'',
				initSync: false,
				forceNoEdit:false,
				forceUpgradeWidgets:false
			}, Object.isObject(options)?options:{}),
			res = {
				err:[],
				hatml:'',
				style:'',
				comment:''
			};
	if (!Object.isObject(d) || d.type != that.data.type ) {
		res.err.push('Error: item of ' + that.data.type + ' type expected.');
		return res;
	}

	id = d._stamp || id;

	var css=['in-item', 'in--'+d.type];
	css.push ((that.Formats[d.format || 'left750'] || {css:''}).css);
	(d.css||[]).forEach(function(e){css.push("in-"+e.toLowerCase().replace(/[^a-z0-9]+/ig,'-'))});
	css.push(!(d.css||[]).length?"in-item-nocss":"in-item-hascss");

	// Prepare manifest

	var man = Object.clone(Object.reject(that, ["Compiler","Hoverpane", "Opts", "Select", "To", "Ico", /^_/]), true),
			data = Object.clone(item, true),
			tid = "in-"+id,
			h="", 
			height,
			insync=false,
			html = [],
			scripts = [];
	man.style[" #xoptions"] = "display:none;";
	man.style[" #tab"] = "display:none;";
	man._compiled = true;

	if (data.source) css.push('in--widget-manifest-'+data.source);

	insync = opts.initSync || data.options.indexOf("init sync")>-1;

	if (data.options.indexOf("noedit")>-1 || (opts.forceNoEdit && data.state=='running')) {
		// Include only executable manifest
		if (data.err.length || !data.valid) res.err.push('Error: widget code is invalid or has errors');
		else {

			var refl = data.options.indexOf("show data")>-1,
					upgrade = (
						that.Widget && (
							opts.forceUpgradeWidgets 
							|| data.options.indexOf('autoupdate') > -1
						)
					)? that.Widget:null,
					fn = (that.Widget && that.Widget.To && that.Widget.To.data) ? that.Widget.To.data:null,
					formdata = data.data,
					formman = upgrade?js2txt(Object.reject(upgrade,['Opts','To']),''):data.js;

			if (
				that.RT
				&& (
				 	(upgrade && upgrade.meta)
					||
					// dirty hack to avoid parsing
					(!upgrade && /("meta"|meta)[\s]?:[\s]?{/.test(data.js))
				)
			) {
				// Prepare meta
				var sdoc = that.RT().data,
						fmeta = sdoc.meta || {};
				fmeta.id = sdoc._id;
				fmeta.tags = sdoc.tags;
				fmeta.theme = Object.reject(Object.merge(
					Object.clone(that.RT().Theme || {}, true), 
					sdoc.style, 
					true
				), ['tags']);
			}

			if (Object.isFunction(fn)) formdata = fn.call(that, data, opts);

			var bg='', bgurl, style = '';

			if (bg = (data.bg || '')) {

				if (/^http[s]?:/.test(bg) || bg.to(1)=='/') bgurl = bg;
				else {
					if (opts.Urls) bgurl = opts.Urls[bg];
					else bgurl = opts.path + bg;
				}
			}

			if (bgurl) style += 'background-image:url(\''+bgurl+'\')';

			h+=[
				'<div id="'+tid+'" class="'+css.join(' ')+'" style="'+style+'">',
					'<div class="in-ct cw-widget">',
						'<div id="xapp" class="'+(refl?' xdata':'')+'">'+data.html+'</div>',
						(refl?'<div id="xreflect" class="xdata"></div>':""),
					'</div>',
				'</div>'
				/*,'<script>',
					'$'+(insync?'':'.my.chain')
				+'(function(){var $f=$("#'+tid+'");$f.find("#xapp").my($.extend('+formman+',{_compiled:true}), ',
						formdata+')',
					(refl?'.on("change.my", function(){'
				 		+'$(this).parent().find("#xreflect").html('
				 		+'cw.lib.js2txt($(this).my("data"),"  ").replace(/^\{/,\'{ <span class="green">// App data</span>\')'
				 		+');'
				 		+'}.debounce(20))':""),
				'.then(function(){$f.css({"height":"", overflow:""});}.debounce(100))',
					'});',
				'<'+'/script>'*/
			].join("");

			html.push(h);

			// First, push manifest
			// if we have widget

			if (upgrade && upgrade.id) scripts.push('$.my.cache('+formman+');');

			// Push starter script

			scripts.push([
				'$'+(insync?'':'.my.chain')
				+'(function(){var $f=$("#'+tid+'");$f.find("#xapp").my($.extend('
				+(upgrade && upgrade.id?'$.my.cache("' + upgrade.id + '")':formman)
				+',{_compiled:true'+(fmeta?',meta:'+JSON.stringify(fmeta):'')+'}), ',
				formdata+')',
				(refl?'.on("change.my", function(){'
				 +'$(this).parent().find("#xreflect").html('
				 +'cw.lib.js2txt($(this).my("data"),"  ").replace(/^\{/,\'{ <span class="green">// App data</span>\')'
				 +');'
				 +'}.debounce(20))':""),
				'.then(function(){$f.css({"height":"", overflow:""});}.debounce(100))',
				'});'
			].join('\n'));
		}

	} else {
		// Include full code
		/*var norepo = !repo[man.id];
		repo[man.id]=true;*/
		var norepo = true;
		h+=[
			'<div id="'+tid+'" class="' + css.join(' ') + '" style=""></div>',
			/*'<script>',
			//(norepo?'$.my.cache('+cw.lib.js2txt(man,"  ")+');' : ''),
			'$'+(insync?'':'.my.chain')
			+'(function(){var $f=$("#'+tid+'"); $f.my('+js2txt(man,"  ")+', ',
				js2txt (data,"  ").replace(/<\//g, '<\\u002F')+')',
			'.then(function(){$f.css({"height":"", overflow:""});}.debounce(100))',
			'});',
			'<'+'/script>'*/
		].join('\n');
		html.push(h);

		scripts.push([
			'$'+(insync?'':'.my.chain')
			+'(function(){var $f=$("#'+tid+'"); $f.my('+js2txt(man,"  ")+', ',
			js2txt (data,"  ").replace(/<\//g, '<\\u002F')+')',
			'.then(function(){$f.css({"height":"", overflow:""});}.debounce(100))',
			'});'
		].join('\n'));

	}

	// - - - - - - - - 

	res.html = html.join('');
	res.scripts = scripts;

	return res;

	//- - - - - - - - - - - - - - -

	function js2txt (srcObj, tabChar){
		// outputs js object as string
		// js2txt(srcObj, tabChar) >> string

		// - - - - - - - - - - - - - - - - - - - - - - -

		function f(n){return n<10?'0'+n:n;}

		var tabs= '\t'.repeat(20),  
				fj = JSON.stringify;

		// - - - - - - - - - - - - - - - - - - - - - - -

		function s2 (w, ctab0, tab){
			var tl=0,a,i,k,l,v,ctab=ctab0||0,xt = tabs;
			if (tab && Object.isString(tab)) {
				tl=(tab+"").length;xt = (tab+"").repeat(20);
			}
			switch((typeof w).substr(0,3)){
				case 'str': return fj(w).replace(/<\/scri/ig,'<\\u002fscri');
				case'num':return isFinite(w)?''+String(w):'null';
				case 'boo': case'nul':return String(w); 
				case 'fun': return _cleanFn(w.toString())
				.replace(/\n([^\t\n])/g,'\n'+(tab?xt.to(ctab*tl+tl):"")+'$1')
				.replace(/\n+/g,'\n')
				.replace(/<\/scri/ig,'<\\u002fscri');
				case 'obj': if(!w) return'null';
					if (Object.isRegExp(w)) return w.toString();
					if (typeof w.toJSON==="function") return s2(w.toJSON(),ctab+(tab?1:0),tab);
					a=[];
					if (Object.isArray(w)){
						for(i=0; i<w.length; i+=1){
							a.push(s2(w[i],ctab+(tab?1:0),tab)||'null');
						}
						return'['
						+a.join(','+(tab?"\n"+xt.to(ctab*tl+tl):""))
						+']';
					}
					for (k in w) if (Object.isString(k)) {
						v=s2(w[k],ctab+(tab?1:0),tab);
						if(v) a.push((tab?"\n"+xt.to(ctab*tl+tl):"")
												 +s2(k,ctab+(tab?1:0),tab)+': '+v);
					};
					return '{'+a.join(',')+(tab?"\n"+xt.to(ctab*tl):"")+'}';
			}
		}

		// - - - - - - - - - - - - - - - - - - - - - - -

		function _cleanFn (s) {
			var splitter = /\)([\s\n\t]+?|\/\/*.*?\*\/|\/\/[^\n]*\n)*?\{/,
					a = s.split(splitter,1),
					head = a[0].from(8).replace(/[\s\n\t]+?|\/\/*.*?\*\/|\/\/[^\n]*\n/g,'')+")",
					tail = "{"+s.from(a[0].length).replace(splitter,'').replace(/}[^\}]+$/,'}');
			return ("function "+head).replace(/^function\sanonymous/,"function ") +" "+tail;
		}

		// - - - - - - - - - - - - - - - - - - - - - - -

		return s2(srcObj,0,tabChar);
	}

}

