To.html:
function (doc,options) {
	var that = this,
			d = doc,
			h = '',
			id = 'x'+Number.random(1e6, 1e7-1)+'',
			opts = Object.merge({
				style:true,
				path:'',
				important:true,
				forceNoEdit:false,
				forceUpgradeWidgets:false,
				initSync:false
			}, Object.isObject(options)?options:{}),
			res = {
				err:[],
				html:'',
				style:'',
				scripts:[],
				comment:''
			},
			astyle,
			isO = Object.isObject,
			isF = Object.isFunction,
			isA = Object.isArray,
			isS = Object.isString,
			Keys = Object.keys;

	if (!isO(d) || d.type != "inliner" || !isA(d.content) || !d.content.length) {
		res.err.push('Error: invalid or empty inliner doc.');
		return res;
	}

	id = d._id || id;

	// Generate stylesheet

	if (opts.style) {

		if (!Object.isObject(opts.style)) {
			astyle = _json2css.call(
				that, 
				null , 
				Object.merge(
					Object.reject(that,['style','data']), 
					{
						style:{
							' ': that.style[' #medium'],
							' .in-item':that.style[' .in-item']
						},
						data: doc
					}
				)
			);
		} else {
			astyle = _json2css.call(
				that, 
				null , 
				Object.merge(
					Object.reject(that,['style','data']), 
					{
						style:{
							' ': that.style[' #medium'],
							' .in-item':that.style[' .in-item']
						},
						data: Object.merge({style:opts.style},Object.reject(doc,'style'), true)
					}
				)
			);
		}

		if (astyle) {
			res.style = astyle[1]
			.map(function(e){
				return (
					'#in-doc-'+id+' '
					+e.replace(/redactor_box/g,'in-cb')
						.replace(/redactor_editor/g,'in-cc')
				).replace(/\s+/g,' ');
			}).join('\n');
		}
	} //-- end stylesheet gen

	// Render content

	d.content.forEach(function(item){
		var type = item.type,
				T = that.Items[type], 
				errs,
				iopts = {
					path:opts.path,
					style:null,
					initSync:opts.initSync,
					forceNoEdit:opts.forceNoEdit
				},
				row;

		if (T && T.To && T.To.html) {

			if (opts.Urls) iopts.Urls = opts.Urls;

			try {
				row = T.To.html.call(
					type=='widget'&&item.source
						?Object.merge({
							Widget:that.Widgets[item.source]||null, 
							RT:function(){return that;}
						},T)
						:T, 
					item, 
					iopts
				);
			} catch (e) { errs = e }

			if (row) {

				if (!row.err.length) {
					res.html += row.html;
					res.style += row.style;
					res.scripts = res.scripts.add(row.scripts||[]);
				} else {
					res.err = res.err.add(row.err || []);
					if (!row.html) {
						res.html +='<div class="in-render-error red hide">'+(row.err||['Error rendering item']).join('<br>')+'</div>';
					}
					else res.html += row.html;
				}
			} else {
				res.html +='<div class="red">'+[errs.message, errs.stack].join('<br>')+'</div>'
			}
		}
	});
	//--end render content


	// Gen HTML string

	res.html = [
		'<div id="in-doc-'+d._id+'" ',
		'class="in-doc-body" ',
		'data-title="'+(d.name+'').escapeHTML()+'" ',
		'data-tags="'+d.tags.map(function(e){return (e+'').replace(/\s+/g,'_').escapeHTML()}).join(" ")+'" ',
		'data-lastupdated="'+(d.log && d.log.length?d.log[0][0]:d.stamp)+'" ',
		Object.keys(d.meta).map(function(i){
			if (Object.isString(d.meta[i])) return 'data-'+i.escapeHTML()+'="'+(d.meta[i]).escapeHTML()+'"'
		}).compact().join(' '),
		'>',
		res.html,
		'</div>'
	].join('').replace(/redactor_box/g,'in-cb').replace(/redactor_editor/g,'in-cc');


	// Dedup scripts

	var nscripts = [],
			hscripts = {};
	res.scripts.forEach(function(e){
		var h = _sdbmS(e);
		if (!hscripts[h]) {
			hscripts[h] = true;
			nscripts.push(e);
		}
	});
	res.scripts = nscripts;


	return res;


	// - - - - - - - - - - - - - - -

	function _json2css ($o, manifest, localOnly) {
		// converts .style section of manifest
		// into two css rule lists for the form
		var  aglob=[], aloc=[], man=manifest;
		if (!isO(man) || !isO(man.style)) return "";

		crawl(manifest.style, "", aglob, aloc);		

		return [_dedup(aglob), _dedup(aloc)];

		//- - - - - - - - - - - - - - 

		function _dedup(a) {
			// replaces rules with identical tails 
			// to #id1,#id2,id#{rule}
			// Todo: make more effective
			return a.map(function(e, i){
				var e1 = a[i+1];
				if (e1) {
					var es0 = e.split('{'),
							es1 = e1.split('{');
					if (es0[1] && es0.from(1)+'' == es1.from(1)+'') return es0[0]+',';
				}
				return e;
			});
		}

		//- - - - - - - - - - - - - - 

		function crawl (branch0, key0, aglob, aloc){
			var i, j, k, b, a, 
					branch = branch0,
					isMedia = /@/.test(key0),
					key = key0.split("@")[0],
					isFn = isF(branch);

			if (isMedia) (isFn?aloc:aglob).push ("@"+key0.split("@")[1].trim()+" {");

			if (isS(branch)) {
				aglob.push(key+(/\{/.test(branch)?branch:'{'+branch.replace(/[\r\n]/g,'')+'}'));
			}
			if (isA(branch) && branch.length) {
				for (i=0; i<branch.length; i++) crawl(branch[i], key, aglob, aloc);
			}
			else if (isO(branch)) {
				k = Keys(branch);
				for (i=0; i<k.length; i++) {
					a = unfold(key, k[i]);
					for (j=0; j<a.length; j++) crawl(branch[k[i]], a[j], aglob, aloc);
				}
			}
			else if (isF(branch)) {
				try {
					b = branch.call(manifest, $o, manifest);
					crawl (b, key, aloc, aloc);
				} catch (e) {
					//console.log(e);
				}
			}
			if (isMedia) (isFn?aloc:aglob).push ("}");
		}

		function unfold (key, selector) {
			var pre = "", ext = selector+"", a;
			if (" " === ext.to(1) || /^[a-z]/i.test(ext)) pre = " ";
			a = ext.split(/\s*,\s{0,}/).compact(true);
			if (!a.length) a.push("");
			return a.map(function (e) {return key+pre+e;});
		}
	}

		//- - - - - - - - - - - - 

	function _sdbmS (s){
		// Very fast hash used in Berkeley DB
		for (var hash=0,i=0;i<s.length;i++)
			hash=s.charCodeAt(i)+(hash<<6)+(hash<<16)-hash;
		return (1e11+hash).toString(36);
	}

}

