			tc;


	for (i in mans) {
		v = mans[i];
		if (!hide[v.data.type] && (tc = t2c[v.data.type])) {
			html+='<span data-type="'+v.data.type+'" class="hbtn '+tc.css+'" title="'+tc.title+'"></span>';
		}
	}

	html+='<nobr>';

	// Opts button

	if (mans[type] && mans[type].Opts) {

		tc = t2c._opts;

		html += '<span class="hbtn hbtn-cog" '
		+'data-type="-opts" id="btn-htmlopts" title="'+tc.title+'"></span>';
	}

	// Copy / paste buttons

	tc = t2c._copy;

	html += '<span class="hbtn hbtn-copy" '
	+'data-type="-copy" id="btn-copy" title="'+tc.title+'"></span>';

	tc = t2c._paste;

	html += '<span class="hbtn hbtn-paste" '
	+'data-type="-paste" id="btn-paste" title="'+tc.title+'"></span>';

	// Del button

	tc = t2c._del;

	if (del) html += '<span class="hbtn hbtn-del" '
	+'data-type="-" id="btn-del" title="'+tc.title+'"></span>';

	// End hoverpane html
	html+='</nobr>';

	html+='</div>';
	return html;
}

Ed.Actions:
function ($o,form) {

	// Hoverpane actions

	var that = this, i,
			d = form.data,
			now = Date.now(),
			$ho=$o.find("#hoverpane:eq(0)"),
			$me = $o.find("#medium"),
			isO = Object.isObject,
			isA = Object.isArray,
			isF = Object.isFunction;

	$o.on("click.my", ".cw-hoverpane-insert .hbtn", function (){
		var xd = $ho.data("hoverpane"),
				$r = xd.form,
				idx = ($r.data("formlist")?$r.data("formlist").index:$o.my("data").content.length+1),
				type = $(this).data("type"),
				idata = $r.my("data"),
				$frame;

		if (type=="-") {
			//$r.trigger("remove");
			d.content = that.CollapseItems(d.content.removeAt(idx));
			$o.my("redraw");
			_off();
		}
		else if (type=="-opts") {
			$ho.addClass("hover");
			$('body>div.redactor_air').css({display:'none'});
			// Add red frame
			if (xd.val && !xd.val.hasClass('redactor_box')) $frame = xd.val;
			else $frame = $r.find('.in-ct');
			$frame.css({outline: '1px dashed red'});

			if (that.Items[idata.type].Opts) {
				var modal = $(this).modal({
					root:$o.parents(".my-form").eq(0),
					manifest:$.extend(
						true,
						{
							RT: function(){return that;},
							Att: that.Att
						},
						$r.my("manifest").Opts
					),
					data:Object.clone($r.my("data"), true),
					//nose:"top",
					screen:"rgba(255,255,255,0.5)",
					align:"top:-4px;left:-"+($(this).parent().position().left+1)+"px;",
					css:"blue lh150 fs80 p10",
					bound:8,
					esc:true,
					close:false
				});

				if (idata.type == "html") {
					// HTML data, special case
					modal.then(function(res){
						_noframe();
						var prevcss = ($r.my("data").css||[]).sortBy()+$r.my("data").format;
						if (res.css + res.format != prevcss) {

							if (prevcss!=$r.my("data").format+"" || $r.find(".redactor_editor>*").size()==1) {
								// Reformat already formatted block
								$r.my("data").css = res.css;
								$r.my("data").format = res.format+"";
								$r.my("redraw");
							} else {
								// Insert new only into unformatted block
								that.Insert("html", idx, xd.p, xd.after, xd.val, {css:res.css});
							}
						}
						_off();
					});

				} else if (idata.type == "widget"){

					// do we need to restart widget ?
					modal.then(function(res){
						$.extend($r.my("data"), res);
						$r.my("redraw");
						//$r.my('find', '#btn-run').trigger('click');
						$r.my("restyle");
						_off();
					});

				} else {
					// Other data, extend data with result
					modal.then(function(res){
						$.extend($r.my("data"), res);
						$r.my("redraw");
						$r.my("restyle");
						_off();
					});
				}

				modal.fail(_off);
			}
		}
		else if (type=="-copy") {

			var clip = $.extend(true, {_id:"_local/cw.Inliner", type:"clip"}, {clip:idata, previews:{}, files:{}});

			if (idata.type=="html" && !(idata.css||[]).length) {clip.clip.data = xd.val[0].outerHTML}

			that.db.get("_local/cw.Inliner")
			.then(
				function(old){ clip._rev = old._rev; return _saveClip(clip)},
				function(err){ return _saveClip(clip) }
			)
			.then(function(){
				cw.lib.note(that.Lang.CLIP_STASHED,"ok");
				_off();
			})

		}
		else if (type=="-paste") {
			that.db.get("_local/cw.Inliner")
			.then(
				function(clip){ 
					if (clip.clip && clip.clip.type) {
						clip.clip._stamp = cw.lib.hash8(Date.now()+"x"+Number.random(1,1e6));
						var item = clip.clip;
						item._new = false;

						if (clip.previews) d.previews = $.extend(d.previews||{}, clip.previews);
						if (Object.size(clip.files)) {
							if (!d._attachments) d._attachments = {};
							$.extend(d._attachments, clip.files);
							that.Att(true);
						}

						that.Insert(item.type, idx, null, false, xd.val, item);

					}
					_off();
				},
				function(err){ cw.lib.note(that.Lang.CLIP_NOCLIP,"error"); _off(); }
			)
		}
		else if (type.to(1)!="-") {
			if (type=="html") {
				that.Insert(type, idx, void 0, false, null, {data:'<p><br></p>'});
				_off();
			} 
			else if (type == "widget") {
				if (Object.size(that.Widgets)) {
					// We have extenders
					$ho.addClass("hover");
					$(this).modal({
						root:$o.parents(".my-form").eq(0),
						manifest:that.Items.widget.Select,
						data:{widgets:Object.values(that.Widgets)},
						nose:"top",
						screen:"rgba(255,255,255,0.5)",
						align:"top:22px; ",
						css:"blue lh150 fs80 p10",
						bound:8,
						esc:true,
						close:false
					})
					.then(function(res){
						that.Insert(type, idx, xd.p, xd.after, xd.val, res.extender);
					})
					.always(_off);
				} else {
					that.Insert(type, idx, xd.p, xd.after, xd.val);
					_off();
				}
			}
			else {
				that.Insert(type, idx, xd.p, xd.after, xd.val);
				_off();
			}
		}

		//- - - - - - - - - - - - - - -

		function _noframe(){
			if ($frame) $frame.css({outline: ''});
		}

		function _off(){ 
			_noframe();
			$ho.removeClass("cw-half hover").css({top:"-250px",left:0});
		}

		function _saveClip(doc){ 

			// here we should detect attaches used, 
			// load em and append to doc as files{},
			// and their previews as previews{}
			var a = d._attachments,
					p = d.previews,
					c = doc.clip,
					list = {},
					wlist,
					man,
					data,
					re = /^http[s]?:\/\//;

			if (!a || !Object.size(a)) return that.db.put(doc); 

			if (c.type=='img' && c.ref && !re.test(c.ref)) list[c.ref] = true;
			else if (c.type == 'widget') {
				if (c.bg && !re.test(c.bg)) list[c.bg] = true;
				// try to parse man
				try {
					man = eval(c.js);
					data = eval(c.data);
				} catch(e){}

				if (isO(man) && isO(data) && man.To && isF(man.To.files)) {
					try {
							wlist = man.To.files.call(man, data);
					} catch(e){}

					if (isO(wlist) && Object.size(wlist)) {
						Object.keys(wlist).forEach(function(k){ list[k] = true; });
					}
				}
			}

			// stash
			var pis = [],
					pi = $.Deferred();

			Object.keys(list).forEach(function(fname){
				var fname = fname;
				if (a[fname]) {
					if (p[fname]) doc.previews[fname] = Object.clone(p[fname], true);
					if (a[fname].data) doc.files[fname] = {
						content_type:a[fname].content_type,
						data:a[fname].data
					} 
					else {
						// fetch attach
						pis.push(that.Att(fname, 'data').then(function(s){
							doc.files[fname] = {
								content_type:a[fname].content_type,
								data:s
							} 
						}));
					};
				}
			});

			if (!pis.length) _save.debounce(1)();
			else {
				$.when.apply(null, pis).done(_save.debounce(1));
			}

			return pi.promise();

			// - - - - - - - - - 

			function _save(){
				that.db.put(doc)
				.then(
					function(){pi.resolve();},
					function(){pi.reject();}
				);
			}

		} // end save clip

	}.debounce(40));

}

