Ed.Insert:
function ($o) {
	var that = this;

	return function  (type, idx, p0, after, $item, extender) {

		// Inserts new item, splits src if needed

		var ins,txt, content, news, 
				a=[], 
				od = $o.my("data"), 
				onew={},
				p=p0;

		if ($item) {
			var $e = $item.parents('.redactor_editor').eq(0),
					id = Number.random(65536, 0x100000 - 1).toString(16).last(4),
					ins = '<b id="split-'+id+'"></b>',
					$i = $(ins).insertBefore($item.eq(0));
			content = $e.html();
			p = ins;
			$i.remove();
		}

		if (p) {
			txt = content || od.content[idx].data;
			a=txt.split(p+((extender||{})._new!==false && type=="html" && $item?$item.eq(0)[0].outerHTML:""));
		}

		if (type) {
			ins = $.extend(true, {_stamp:cw.lib.hash8(Date.now()+"x"+Number.random(1,1e6))}, that.Items[type].data);
			if (type=="html" && $item) {
				ins.data = $item.eq(0)[0].outerHTML;
			}
			if (extender) $.extend(ins, extender);

			if (!(extender && extender._new===false)) ins._new = true;
		}

		if (
			!(type=="html" && $item) &&
			(!p || a.length<2 || (a[0]=="" && !after) || (a[1]=="" && after))
		) {
			if (type) od.content.add(ins, idx+(after?1:0));
			else od.content.removeAt(idx);
		} else if (p) {
			if (type) {
				od.content[idx].data=a[0];
				//od.content[idx]._stamp = (Date.now()+100)+"";
				onew=Object.clone(od.content[idx],true);
				onew.data=a[1];
				onew._stamp = cw.lib.hash8(Date.now()+"x"+Number.random(1,1e6));

				od.content.add([ins,onew], idx+1);

				if (!od.content[idx].data) od.content.removeAt(idx);

				od.content = that.CollapseItems(od.content);

			} else {
				//delete
				if (!a[0].length && !a[1].length) {
					od.content.removeAt(idx+(after?1:0));
				} else {
					od.content[idx].data=a[0]+a[1];
				}
			}
		}
		od.content = that.CollapseItems(od.content);
		$o.find("#medium").trigger("redraw");
	};
}

