#file.bind:
function (d,v,$o) {

			var that = this,
					f,
					$r = $o.my().root,
					$img = $r.find("#source"),
					img="",
					_prepareFname = that.Fname;

			if (v!=null && v) {
				f = $o[0].files[0];
				if (f) (function(){
					d.filename = _prepareFname(f.name);
					d.type = f.type;

					var params = { 
						noRevoke:true, 
						canvas: true,
						pixelRatio: 1,
						downsamplingRatio: 0.5,
						maxWidth:3600,
						maxHeight:2400
					};

					// apply img
					var _apply = function (ni) {
						var st = $img.attr('style');
						$img = $(ni);
						$img.attr('style',st).removeClass("hide");
						$r.find("#source").replaceWith($img);
						$img.attr('id','source');

						$o.parent().addClass("hide");

						d.src.w = $img[0].width;
						d.src.h = $img[0].height;
						d.scale = d.src.w/$img.width();

						$img.Jcrop({
							onChange: preview,
							aspectRatio: d.aspect,
							onSelect: preview.debounce(20),
							bgColor:'#e3e8eb',
							bgOpacity:0.2,
							minSize:[50,40],
							allowMove:true
						}, function (){
							this.animateTo([100,100,300,230]);
						});
						d.cropped = true;
						if (!d.enforceQuality) d.quality = d.type=='image/jpeg'?'0.90':'1';

						that.my.recalc('#fname');
						that.my.check('#fname');
					};

					loadImage.parseMetaData(f, function(data) {
						if (data.exif) params.orientation = data.exif.get('Orientation');
						loadImage(f, _apply, params);
					});

				})();
			} // end if

			function preview(c){
				$.extend(d.crop, Object.select(c,["x","y","w","h"]));
				d.cropped=true;
				that.my.check('#dims');
				that.my.check('#quality');
