Cropper.Fname:
function (fname0,size) {
	var fname = fname0+'',
			sfname = fname.split('.'),
			ln = !isNaN(size)?+size:60;
	if (sfname.length>1)  {
		fname = sfname.to(-1).join('.').truncate(ln,'middle','_') + '.' +sfname.last().toLowerCase();
	} 
	else fname = fname.toLowerCase().truncate(ln,'middle','_');

	// translit cyr and replace invalid chars
	var cr = 'аa бb вv гg дd еe ёyo жzh зz иi йiy кk лl мm нn оo пp рr сs тt уu фf хh цts чch шsh щsch ъ- ыy ь- эe юyu яya'
	.split(" ").reduce(function(a,b){
		var key = b.to(1), val = b.from(1);
		a[key] = val;
		a[key.toUpperCase()] = val.toUpperCase();
		return a; 
	},{});

	fname = fname.split('').map(function(e){
		if (cr[e]) return cr[e];
		else if (!/[a-z0-9\.\-_~]/i.test(e)) return '-';
		return e;
	}).join('').replace(/-+/g,'-').replace(/^_/,'');

	return fname;
}

Cropper.Gen:
function (countOnly) {
	var that = this,
			d = that.data,
			f,
			//$r = $o.my().root,
			$c = that.my.find("#x1"),
			$img = that.my.find("#source"), 
			$c2 = that.my.find("#x2"),
			n={k:d.scale}, img="",
			width=d.width*(+d.expand || 1.7) | 0,
			quality = +d.quality || that.Quality(width) || 0.85,
			s;

	preview(d.crop);

	if (+quality != 1) { 
		s = that.my.find("#x1")[0]
		.toDataURL('image/jpeg', quality)
		.substr(23);
	}
	else {
		s = that.my.find("#x1")[0]
		.toDataURL('image/png', quality)
		.substr(22);
	}

	if (countOnly) return s.length/4*3|0;

	d.filename = that.Fname(d.filename);

	d.data = s;

	d.type = +quality != 1?"image/jpeg":"image/png";

	//- - - - - - - - - - - -

	function preview (c){
		if(parseInt(c.w) > 0) {
			// Show image preview
			var img = $img[0], cs, ctx, cs2, ctx2, 
					k = n.k,
					w = c.w*k|0 , // scale width/height
					h = c.h*k|0; 
			if (w>width*2) {
				cs2 = $c2[0];
				cs2.width=width*2;
				cs2.height=2*width*(h/w)|0;
				ctx2 = cs2.getContext("2d");
				ctx2.rect(0, 0, cs2.width, cs2.height);
				ctx2.fillStyle='white';
				ctx2.fill();
				ctx2.drawImage(img, c.x*k |0, c.y*k|0, w, h, 0, 0, cs2.width, cs2.height);

				cs = $c[0];
				cs.width=width;
				cs.height=width*(h/w)|0;
				ctx = cs.getContext("2d");
				ctx.drawImage(cs2, 0, 0, cs2.width, cs2.height, 0, 0, cs.width, cs.height);
			} else {
				cs = $c[0];
				cs.width=width;
				cs.height=width*(h/w)|0;
				ctx = cs.getContext("2d");
				ctx.rect(0, 0, cs.width, cs.height);
				ctx.fillStyle='white';
				ctx.fill();
				ctx.drawImage(img, c.x*k|0, c.y*k|0, w, h, 0, 0, cs.width, cs.height);
			}
		}
	}
}

