({
	bind:function(d,v,$o) {
		var that = this;
		if (v!=null || d._new) {
			var f = function(d){
				//console.log(d)
				$o.modal({
					manifest:$.extend({Att:that.Att},that.Cropper),
					data:{
						data:"",
						filename:"", 
						width:(d.format+"").replace(/\D/g,"")
					},
					width:1031,
					global:true,
					screen:true,
					esc:true
				}).then(function (crop) {
					if (Object.isObject(crop)) {

						var a = that.Att(null),
								newatt = false,
								mime,
								fname;

						// New image

						if (!crop.ref) {
							if (crop.data.length) {
								fname = crop.filename;
								mime = crop.type;
								if (
									!/^[a-z0-9\-\._~]{1,30}\.(jp[e]?g|gif|png)$/i.test(fname)
								) fname = cw.lib.hash8(fname+Date.now())+'.'+mime.split("/").last().split("+")[0];

								that.Att(fname, {
									content_type:mime,
									data:crop.data
								});

								newatt = true;
							}
							crop.ref = fname;
						}

						// Existing image

						if (crop.ref) {

							mime = mime || a[crop.ref].content_type;
							d.ref = crop.ref;

							// Load image and prepare preview
							that.Att(d.ref, "data")
							.then(function(data){
								var $i = $('<img src=""/>');
								$i.attr("src", "data:"+mime+";base64,"+data);
								$i.load(function(){
									var ico = cw.lib.image($i,"white"),
											dim = ico.width()+'×'+ico.height();
									ico.resample(150);
									if (ico.height()>150) ico.resample(22500/ico.height())
									crop.type = "image/jpeg";
									_refresh(ico.sharpen(0.1).contrast(1.05).jpeg(0.65), dim);
									$i.remove();
								});
							});

						} 

						//- - - - - - - -

						function _refresh(data, dim) {
							$o.html("");
							$('<img class="cw-preview" src=""/>')
							.attr("src",data)
							.appendTo($o);
							d.data=data.split("base64,")[1];

							if (newatt) {
								d.format="left"+crop.width;
								var l = crop.data.length/4*3 |0;
								that.Att(fname, {
									content_type:mime,
									data:crop.data,
									preview:{
										fname:fname,
										size: l.metric(l>1e6?2:0,false).replace(/([a-z])$/i,' $1')+"b",
										desc:'',
										dim:dim,
										data:data
									}
								});
							}

							crop.data="";
							that.my.redraw();
						}
					}
				});
			}.fill(d);
			if (d._new) f.delay(250);
			else f();
			if (d._new) delete d._new;
		}
		if (d.data) {

			if (d.ref) {
				var p = that.Att(d.ref, "url");
				if (Object.isObject(p) && p.then) {
					p.then(function(url){
						//console.log(url.to(100))
						$o.html('<img class="cw-preview" src="'+url+'" />');
						//that.my.redraw();
					})
				}
				else if (Object.isString(p)) {
					return '<img class="cw-preview" src="'+p+'" />';
				}
			}
			return '<img class="cw-preview" src="data:image/jpeg;base64,'+d.data+'" />';
		}
		else {
			return ('<div class="w100p bg-lblue tac p30 fs80 blue">'
							+'<span class="pseudolink">Upload image...</span>'
							+'</div>');
		}
	},
	events:"click.my",
	delay:100
})

