({
	params:{width:320},
	data:{data:"", source:"",format:"left500", css:[]},
	lang:{
		en:{
			BTN_APPLY:'Apply',
			BTN_CANCEL:'Cancel',
			TITLE:'Embed settings',
			SHOWDESC:'Show embed description',
			AUX_PLC:'Aux formatting...',
			EMBED_PLC:'Paste embed code or video URL here'
		},
		ru:{
			BTN_APPLY:'Применить',
			BTN_CANCEL:'Отмена',
			TITLE:'Настройки внешнего кода',
			SHOWDESC:'Показывать текст',
			AUX_PLC:'Дополнительные эффекты...',
			EMBED_PLC:'Вставьте код внешнего ресурса или ссылку на Youtube-видео'
		}
	},
	init:function($o, form){

		var that = this,
				d = form.data,
				lang = 'en';

		// L10N
		var L = that.Lang = that.lang;

		$o.html($.my.formgen([
			{row:"320px", label:"70px", rowCss:"my-row pb10", labelCss:"my-label fs90 gray"},
			'<div class="blue-1 fs130 ml3 mb7">{TITLE}</div>',
			['', '<div class="pl3">','txt#edata.fs90',{plc:'{EMBED_PLC}', spellcheck:"false"},'</div>'],
			'<div id="format" class="tag-in-fmt mb-2"></div>',
			'<div id="ecss" class="tag-in-fmt mb3"></div>',
			['', 'inp#css.w100p.fs100'],
			'<label class="fs90 ml4 xgray"><input type="checkbox" id="showdesc" value="show"> {SHOWDESC}</label>',
			{rowCss:"my-row mt10 ml3"},
			['',
			 'but#btn-apply.w100.green.mr5',{txt:'{BTN_APPLY}'},
			 'but#btn-cancel.w100',{txt:'{BTN_CANCEL}'}
			]
		]).assign(L));

		this.Start = form.data.format + form.data.showdesc;

		if (d.data && !d.source ) d.source = d.data;

				var a = ['small normal large', 'background frame rules', 'accented'],
				b = a.join(" ")+" ",
				ecss = [],
				css = [];

		var c = (d.css||[]), isNormal = true;
		c.forEach(function(i){
			if (b.indexOf(i.toLowerCase()+" ")>-1) ecss.push(i);
			else css.push(i);
			if (i=="Small" || i=="Large") isNormal = false;
		});

		if (isNormal) ecss.push("Normal");

		that.Ecss = ecss;
		d.css = css;
	},
	ui:{
		"#edata":{
			bind:	function(d,v,$o){
				var that=this

				if (v!=null) d.source = v;

				var s = d.source, 
							id="", 
							res="",
							provider = '',
							formats = that.Formats;

				// We have embed

				if (!/^http[s]?:\/\//.test(d.source)) {

					// Detect embed provider

					if (/youtu[\.]?be/.test(s)) {
						res = _redim(s);
						provider = 'youtube';
					}

					else if (/google\.com\/maps\/embed/.test(s)){
						res = _redim(s);
						provider = 'gmap';
					}

					else if (/gist\.github/.test(s)) {
						res = '<div class="in---gist" style="display:none">'+s.replace(/^([^"]+")([^"]+)(".+)$/,'$2')+'</div>';
						res+=[
							'<sc'+'ript>',
							'$(function(){',
								'if (!window.GIST_LOADING) { ',
									'window.GIST_LOADING = 0;',
									'$(".in---gist").each(function(){var that = $(this), $p = that.parent();',
										'window.GIST_LOADING +=1;',
										'$.getJSON( that.text() + "on?callback=?", function( data ) {',
											'window.GIST_LOADING -=1;',
											'$p.html(data.div);var url = data.stylesheet,$head = $("head");',
											'if( $head.find(\'link[rel="stylesheet"][href="\'+url+\'"]\').length < 1 ) ',
											'$head.append(\'<link rel="stylesheet" href="\'+ url +\'" type="text/css" />\');',
											'});',
									'});',
								'}',
							'});',
							String.fromCharCode(60,47)+'script>'
						].join('\n');
						provider = 'gist';
					}

					else if (/jsfiddle\.net/.test(s)) {
						if (s.substr(0,7) == '<script') {
							res = '<iframe width="100%" height="300" src="'
								+s.replace(/^([^"]+")([^"]+)(".+)$/,'$2').replace('embed/','embedded/')
								+'" allowfullscreen="allowfullscreen" frameborder="0"></iframe>';
						} 
						else res = s;
						provider = 'jsfiddle';
					}

					else if (/class="twitter\-tweet"/.test(s)) {
						res = _redim(s);
						provider = 'twitter';
					}

					d.data = res || d.source;
					d.provider = provider || 'unknown'
				}

				// We have URL

				else {

					// ####  Youtube  ####

					if (/youtu[\.]?be/.test(s)) {
						if (/^https:\/\/youtu\.be/.test(s)) {
							id = s.split(/[\/\?&]/)[3];
						}
						else if (/^http[s]?:\/\/(www.)?youtube.[a-z]{2,3}\/watch/.test(s)) {
							id = (s.match(/[\?&]v=([a-z0-9\-_]+)/i) || [])[1];
						}
						if (id) {

							res = '<iframe src="https://www.youtube.com/embed/'
								+id+'?rel=0&amp;showinfo=0" frameborder="0" '
								+' allowfullscreen></iframe>';
							provider = 'youtube';
						}
					}

					// ####  Image  ####

					else if (/\.(jpeg|jpg|png|gif)$/i.test(s)) {
						res = [
							'<div class="w100p tac">',
								'<img src="'+s+'" ',
								' style="max-width:100%!important;max-height:1000px;" ',
								'/>',
							'</div>'
							].join("");
						provider = 'image';
					}

					// ... no match
					if (!res) {
						d.data = [
							'<div class="w100p bg-lblue tac p30 fs80">',
								'<a target="_blank" href="'+s+'">',
									s.truncate(50,"middle"," … "),
								'</a>',
							'</div>'
							].join("");
						d.provider = 'unknown';
					} else {
						d.data = res;
						d.provider = provider;
					}

				}
				return d.source;

				//--------

				function _redim(s) {
					return s.replace(/width\s?=\s?"\d+[%]?"/i,'').replace(/height\s?=\s?"\d+[%]?"/i,'');
				}

			},
			watch:"#format",
			css:{
				"self:fs90":function(d){return d.source.length<150},
				"self:fs80":function(d){return d.source.length>=150}
			}
		},
		"#format":{
			bind:function (d,v,$o) {
				if (v!=null) {
					d.format=v;
					return v;
				}
				return d.format;
			},
			init: function ($o) {
				var that=this, formats = that.Formats,
						e0=Object.keys(formats)[0];

				$o.tags({
					tags:[
						Object.keys(formats).slice(1)
						.map(function(e){
							var tag={
								name:'<span class="t-'+e+'"></span>',
								value:e
							};
							return tag;
						})
					],
					empty:{
						name:'<span class="t-'+e0+'"></span>',
						value:e0
					},
					tagcss:"tag cp",
					tagshim:"",
					groupshim:""
				});
			}
		},
		"#showdesc":"showdesc",
		"#ecss":{
			init: function($o, form){
				var a = ['small normal large', 'background frame rules','accented'];
				$o.tags({
					tags: a.map(function(e){
						return e.split(" ").map(function(i){
							return {name:'<span class="t-'+i+'"></span>', value:i.capitalize(), title:i.capitalize()};
						});
					}),
					tagcss:"tag cp",
					tagshim:"",
					groupshim:""
				});
			},
			bind:"this.Ecss"
		},
		"#css":{
			init: function($o){
				$o.select2({
					tags:["Darken","Parallax"],
					placeholder:this.Lang.AUX_PLC
				});
			},
			bind:"css"
		},
		"#btn-apply":function(d,v,$o){
			if(v!=null){
				d.css = d.css.add(this.Ecss||[]).unique().remove("Normal");
				$o.trigger("commit");
			}
		},
		"#btn-cancel":function(d,v,$o){if(v!=null)$o.trigger("cancel")}
	},
	style:{
		" #edata":'height:120px;overflow-x:hidden;overflow-y:auto;resize: none'
	},
	Ecss:[]
})

