({
	id:"cw.Inliner.Ed.Items.widget",
	//Name:"App",
	Name:'<span class="fi-layout fs115 dib vat mt1 mb-1" title="Add widget"></span>',
	Hoverpane:".in--widget .in-ct",
	inherit:["Att"],
	data:{
		name: "",
		type: "widget",
		tab:["manifest"],
		js:"// Widget manifest\n({\n\tinit: function ($form, form) {\n\t\t// Init code\n\t},\n\tui: "
		+"{\n\t\t// UI bindings\n\t}\t,\n\tstyle: {\n\t\t// Local CSS rules\n\t}\n})",
		html:"",
		data:"({\n\t//Put default data here\n})",
		state:"",
		valid:false,
		err:[],
		options:[],
		source:"",
		format:"left750", 
		css:["Frame"],
		bg:''
	},
	Url:{},
	init: function ($o, form) {
		var that = this,
				d= form.data,
				data = d.data,
				fmt = d.format,
				style = that.Formats[fmt];

		$o.formgen(that.HTML);

		$o.addClass("in--widget "+style.css);
		if (that.Opts) that.Opts.Formats = that.Formats;

		$o.then(function(){
			if (form.data.state) $o.find("#btn-run").trigger("click");
			else that.XCode($o, form.data);

			$o.find("#ovl").removeClass('hide');

		});
	},
	die:function($o){
		if ($o.find("#xapp").data("my")) {
			try{
				$o.find("#xapp").my("remove");
			} catch(e) {}
		}
	},
	ui:{
		"#formatter":{
			bind:function (d,v,$o) {
				var that = this, 
						$r, fm;
				$r = $o.my().root;
				fm = this.Formats[d.format];

				var css= $r.attr("class").split(" ").map(function(e){
					if (!/^in-/.test(e)) return e;
				}).compact(true);

				if (d.state == "running") {

					(d.css||[]).forEach(function(e){css.push("in-"+e.toLowerCase().replace(/[^a-z0-9]+/ig,'-'))});
					css.push(!(d.css||[]).length?"in-item-nocss":"in-item-hascss");
					css.push (fm.css);

				} else {
					css.push("in-frame in-item-wide")
				}
				$r.attr("class", css.join(" ")+" in--widget in-item");

				// bg
				if (d.bg && d.state == "running") {
					$r.css({
						'background-image':'url("'+(that.Url[d.bg] || d.bg)+'")'
					});
				} else {
					$r.css({
						'background-image':'none'
					});
				}

			},
			watch:"#btn-run,#btn-code"
		},
		"#ovl-tab":{
			bind:function (d,v,$o){
				if (null!=v) {
					var $x = $o.my("find","#xcode");
					if ($x.is(".my-form")) $x.my("redraw");
					d.tab = v.length?v:['manifest'];
				}
				return d.tab;
			},
			init: function ($o) {
				$o.tags({tags:[{"HTML":"html"}, {"Manifest":"manifest"}, {"Defaults":"data"}]})
			}
		},
		"#btn-run":{
			bind: function (d,v,$o){
				var that = this;
				if (v!=null) {

					if (d.state == "running") $o.my("find","#xapp").my('remove');

					if (!that.Att || !d.bg || /^http/.test(d.bg)) _run();
					else {
						var p = that.Att(d.bg, 'url');
						if (p.then) p.then(function(url){
							that.Url[d.bg] = url;
							_run();
						});
						else {
							that.Url[d.bg] = p;
							_run();
						}
					}
				}

				// Run
				function _run() {
					if (d.valid) {
						d.state = "running";
						$o.my("find","#xcode,.ovl-tabs").addClass("hide");
						var $a = $o.my("find","#xapp"),
								$v = $o.my("find","#xreflect"),
								man = eval(d.js);
						man._compiled = this._compiled;

						if (that.Att) man.Att = that.Att;
						if (that.db) man.db = that.db;
						if (man.meta && that.RT) {
							man.meta = Object.merge(
								Object.clone(that.RT().Config.meta || {}, true),
								Object.clone(that.RT().data.meta || {}, true)
							);
							man.meta.id = that.RT().data._id;
							man.meta.theme = Object.reject($.extend(true,{},that.RT().Theme, that.RT().data.style),['tags']);
							man.meta.tags = Object.clone(that.RT().data.tags, true);
						}

						$a.removeClass("hide")
						.html(d.html)
						.my(man, eval(d.data) || {})
						.then(function(){
							$a.trigger("change");
						}.debounce(50))
						.then(function(){
							$a.my('restyle');
						}.debounce(200));

						if (d.options.indexOf("show data")!=-1) {
							$a.on("change.my", function(){
								$v.html(
									cw.lib.js2txt(
										$a.my("data"),"  ")
										.escapeHTML()
										.replace(/^\{/,'{ <span class="green">// Form data</span>')	
								);
							}.debounce(2));
							$a.addClass("xdata");
							$v.addClass("xdata");
						} else {
							$a.removeClass("xdata");
							$v.removeClass("xdata");
						}

						// Persist data 2 doc
						$a.on("change.my", function(){
							if (d.options.indexOf("reflect")!=-1) {
								d.data = "("+JSON.stringify($a.my("data"), null, '\t')+")";
							}
						}.debounce(50));

					} else {
						cw.lib.note(d.err.join(".<br>")+".", "error", 5000);
					}
				}

			},
			css:{
				hide: function(d){return !!d.state},
				"red": function (d) {return !d.valid},
				"green": function (d) {return d.valid}
			},
			events:"click.my",
			watch:"#btn-code"
		},
		"#btn-code":{
			bind: function(d,v, $o){
				if (v!=null) {
					d.state = "";
					$o.my("find","#xapp").addClass("hide").my("remove");
					$o.my("find","#xcode,.ovl-tabs").removeClass("hide");
					$o.my("find","#xreflect").removeClass("xdata");
					var $x = $o.my("find","#xcode");
					if (!$x.is(".my-form")) {
						this.XCode($o.my().root, d);
					} else {
						$o.my("find","#data").trigger("recalc");
					}
				}
			},
			css:{
				hide: function(d){return !d.state || d.options.indexOf("noedit")>-1}
			},
			events:"click.my",
			watch:"#btn-run"
		},
		"#warn":{
			bind:function (d) {
				var t=d.js, o, err=[], data, isO = Object.isObject;
				try { o = eval(t); } catch (e) {}
				try { data = eval(d.data); } catch (e) {}
				d.valid=false;
				if (!isO(o)) err.push("Invalid manifest");
				else {
						if ( !isO(o.ui)) err.push("No ui section");
						else if ( !Object.size(o.ui)) err.push("No bindings in ui section");

						if  ( !Object.isFunction(o.init) && !Object.isArray(o.init) && !(d.html+"").length)
							err.push("No HTML defined in init function or HTML tab");

						if (!isO(data))  err.push("Defaults must be an object");

						if (o.data!==undefined && !isO(o.data)) err.push("Section ‘data’ must be an object");
						if (o.params!==undefined && !isO(o.params)) err.push("Section ‘params’ must be an object");
						if (o.style!==undefined && !isO(o.style)) err.push("Section ‘style’ must be an object");
					}
				if (!err.length) d.valid = true;
				d.err=err;
			},
			delay:500,
			recalc:"#btn-run"
		}
	},
	XCode: function ($form, d){
		this.XCode3._compiled = this._compiled;

		$form.find("#xcode").removeClass('hide')
		.my (this.XCode3, d)
		.on("change", function(){
			$form.find("#warn").trigger("check.my");
		});
	},
	XCode3:{
		ui:{
			"#xjs":{css:{hide: function (d) { return d.tab.indexOf("manifest")==-1;}}},
			"#js":{
				bind:"js",
				init: function ($o, form){ 
					if (!this._compiled || form.data.tab.indexOf("manifest")>-1) this.Cm($o, "javascript", true);
				}
			},
			"#xdata":{
				css:{
					hide: function (d) { return d.tab.indexOf("data")==-1;},
					bt1: function (d)  { return d.tab.length>1;}
				}
			},
			"#data":{
				bind:"data",
				init: function ($o, form){ 
					if (!this._compiled || form.data.tab.indexOf("data")>-1) this.Cm($o, "javascript", true);
				}
			},
			"#xhtml":{
				css:{
					hide: function (d) { return d.tab.indexOf("html")==-1;},
					bb1: function (d)  { return d.tab.length>1;}
				}
			},
			"#html":{
				bind:"html",
				init: function ($o, form){ 
					if (!this._compiled || form.data.tab.indexOf("html")>-1) 
						this.Cm($o, "htmlmixed", true, "base16-light");
				}
			}
		},
		Cm: function ($o, mode, lineNums, theme) {
			CodeMirror.fromTextArea($o[0], {
				mode: mode,
				lineNumbers:lineNums,
				indentWithTabs:!0,
				indentUnit:2,
				tabSize:2,
				theme: theme||"default"
			});
		}
	},
	HTML:[
		'<div id="formatter" class="hide"></div>',
		'<div class="cw-widget in-ct">',
			'<div id="ovl" class="hide">',
				'<div class="ovl-tabs">',
					'<span id="ovl-tab"></span>',
				'</div>',
				'<div class="ovl-buttons">',
					'<span id="btn-run"><span style="font-size:80%">▶︎ </span>Run</span>',
					'<span id="btn-code" class="hide">Code&thinsp;✎</span>',
				'</div>',
			'</div>',
			'<div id="xcode" class="hide">',
				'<div id="xhtml"><textarea id="html"></textarea></div>',
				'<div id="xjs"><textarea id="js"></textarea></div>',	
				'<div id="xdata"><textarea id="data"></textarea></div>',
			'</div>',
			'<div id="xapp" class="hide"></div>',
			'<div id="xreflect"></div>',
			'<div id="warn" class="hide"></div>',
		'</div>'
	],
	Formats:{
		left300:{css:"in-item-small"},
		left500:{css:"in-item-medium"},
		left750:{css:"in-item-wide"},
