(function ($o, form) {

	var that = this, 
			i, old,
			d = form.data,
			now = Date.now(),
			lang = 'en';

	// Locale
	// if (cw.me && that.Lang[cw.me('locale')]) lang = cw.me('locale');
	// that.Lang = that.Lang[lang];
	that.Lang = that.lang;

	// Prepare doc
	if (form.data.title) delete form.data.title;

	for (i=0;i<d.content.length;i++) {
		if (!d.content[i]._stamp || d.content[i]._stamp=='1101') {
			d.content[i]._stamp = cw.lib.hash8(Date.now()+"x"+Number.random(1,1e6));
		}
		if (d.content[i]._new) delete d.content[i]._new;

		// translate type:code to widget:in.Code
		if (d.content[i].type=='code') {	
			old = d.content[i];
			d.content[i] = {
				_stamp:old._stamp, type:'widget', js:'('+cw.lib.js2txt(that.Widgets.Code)+')', html:"",
				data:'('+JSON.stringify({ type:"inliner-codesample", tab:old.tab, js:old.js, html:old.html, css:old.css})+')',
				state:"running", tab:["data"], options:["noedit","reflect","autoupdate"],
				valid:true, err:[], source:"Code", format:"left500", css:[], bg:''
			};
		}

	}

	// Remove previews for non-existing attaches

	if (d.previews) Object.keys(d.previews).forEach(function(i){
		if (!(d._attachments||{})[i]) delete d.previews[i];
	});

	// Build DOM
	$o.formgen('init', that.HTML.assign(that.Lang));

	if (!$o.find("#hoverpane:eq(0)").size()) {
		$($(that.Hoverpane()).attr("id","hoverpane")[0].outerHTML)
		.prependTo($o);
	}

	// Build Hoverpane sceleton
	var $ho=$o.find("#hoverpane:eq(0)"),
			$me = $o.find("#medium"),
			hh = $ho.height(),
			dx = $o.offset().left,
			dy = $o.offset().top;

	// ---------------------------

	that.Insert = that.Insert($o);

	// ---------------------------

	// Update widgets code
	that.UpdateWidgets(d);

	// ---------------------------

	// Hoverpane actions
	that.Actions($o, form);

	// ---------------------------

	// Hoverpane tracker
	$me.on(
		"mousemove.my", 
		Object.values(that.Items)
		.map(function(e){return e.Hoverpane})
		.compact()
		.join(", "), 
		function (evt) {
			var dx = $o.offset().left,
					dy = $o.offset().top,
					scy = $o.scrollTop(),
					$x = $(this), 
					ofs = $x.offset(), 
					h=$x.height(),
					ex = evt.clientX, 
					ey = evt.clientY,
					x = ofs.left,
					y = ofs.top, 
					after=false, 
					fulltop=0, 
					$form, type, ohtml="", fhtml="", showDel = true;

			hh = $ho.height();
			//correct pos if we inside Redactor, that has floating content
			if ($x.is(".redactor_box *")) {
				//insert and del small shim
				var $i=$('<b>\u200b</b>').prependTo($x);
				x=$i.offset().left;
				$i.remove();
			}

			fulltop = y-dy-hh+scy-2; 
			if ($ho.data("hoverpane") && $ho.data("hoverpane").top && Math.abs($ho.data("hoverpane").top-fulltop)<3) 
				fulltop = $ho.data("hoverpane").top;

			$ho.css({top:(fulltop | 0)+"px", left:(x-dx-23)+"px"});

			$ho.toggleClass("cw-half", (ex-x<100 /*&& Math.abs(y+h/2-ey+scy)-h/2<40*/));

			$form = $x.parents(".my-form:eq(0)");
			type = $form.my("data").type;


			var prev = d.content[$form.data("formlist").index-1],
					curr = $form.my("data"),
					hide = {};

			if (type=="html") {
				ohtml=$x[0].outerHTML;
				fhtml=curr.data;
				// Allow del for empty and non-last html items
				showDel = !!(

					that.data.content.last() !== curr
					&& (
						(fhtml===ohtml && ohtml.stripTags()==="")
						|| (curr.css||[]).length
					)
				);
			}

			if (curr.type == "html" && !(curr.css||[]).length) hide.html = true;
			if (curr.type != "html") {
				if (prev && prev.type=="html" && !(prev.css||[]).length) hide.html = true;
			}

			$ho.html($(that.Hoverpane(type, showDel, hide)).html());

			if (type==="html") {
				$ho.data("hoverpane",{
					form:$form, 
					p:" ", 
					after:after, 
					top:fulltop, 
					val:$x,
					prev:prev
				});
			}
			else $ho.data("hoverpane",{
				form:$form, 
				after:after, 
				top:fulltop
			});		

		}.debounce(50)
	);
})

