						}

						that.Insert(item.type, idx, null, false, xd.val, item);

					}
					_off();
				},
				function(err){ cw.lib.note(that.Lang.CLIP_NOCLIP,"error"); _off(); }
			)
		}
		else if (type.to(1)!="-") {
			if (type=="html") {
				that.Insert(type, idx, void 0, false, null, {data:'<p><br></p>'});
				_off();
			} 
			else if (type == "widget") {
				if (Object.size(that.Widgets)) {
					// We have extenders
					$ho.addClass("hover");
					$(this).modal({
						root:$o.parents(".my-form").eq(0),
						manifest:that.Items.widget.Select,
						data:{widgets:Object.values(that.Widgets)},
						nose:"top",
						screen:"rgba(255,255,255,0.5)",
