					.always(_off);
				} else {
					that.Insert(type, idx, xd.p, xd.after, xd.val);
					_off();
				}
			}
			else {
				that.Insert(type, idx, xd.p, xd.after, xd.val);
				_off();
			}
		}

		//- - - - - - - - - - - - - - -

		function _noframe(){
