				if (f) (function(){
					d.filename = _prepareFname(f.name);
					d.type = f.type;

					var params = { 
						noRevoke:true, 
						canvas: true,
						pixelRatio: 1,
						downsamplingRatio: 0.5,
						maxWidth:3600,
						maxHeight:2400
					};

					// apply img
					var _apply = function (ni) {
						var st = $img.attr('style');
