			$row.css({'border':'1px solid '+$row.my("data").color})
		});
		$o.on("mouseleave.my", "path.sector", function(){
			var id=$(this).attr("id"),
					$row = $o.my("find","#slist>div").eq(id.from(1));
			$row.css({'border':'1px solid transparent'})
		});

				},
