		var a, sum;
		a = d.rows.reduce(function(a, elt){
			if (!isNaN(parseFloat(elt.value.replace(/,/g,'.')))) {
				var t = Object.clone(elt);
				t.value = parseFloat(elt.value.replace(/,/g,'.'));
				a.push(t)
			};
			return a;
		},[]);
		sum = a.reduce(function(a, elt){ return a+elt.value; 	},0);

		var rot = -90,
				w = Math.min(d.width, d.height, $o.my("find","#legend").height()).clamp(230,d.width),
