		a.forEach(function(e, i){ 	
			var x1,y1,x2,y2,end;
			e.angle = a.length==1 ? 359.9999 : e.value/sum*360; 
			end = rot + e.angle;

			x1 = cx - ow*cos(PI*rot/180);
			y1 = cy - ow*sin(PI*rot/180);

			x2 = cx - ow*Math.cos(PI*end/180);
			y2 = cy - ow*Math.sin(PI*end/180);

			rot = end;

			h+='<path '
			+'id="x'+i+'" class="sector" '
			+'d="M'+x1+','+y1+' A'+ow+','+ow+' 0 '
			+(e.angle>180?1:0)+',1 '+x2+','+y2+' " '
			+'stroke-width="'+aw+'" fill="none" stroke="'+e.color+'" '
			+'/>';
		});
		h+='</svg>'
		return h;

				},
			"watch": "#slist"
		}
