manifest.MakeList:
function () {
	var that = this,
			d = that.data,
			items = d.items,
			item = {
				bg:d.bg,
				padding:d.gap,
				border:d.border,
				align:d.align+'',
				scale:100,
				ratio:'16:9',
				padPreview:false
			};
	if (!items.length) {
		that.List = []; 
		return;
	}

	that.List = items.map(function(e){ return $.extend(true, {}, item, e); });

}

manifest.GetAtts:
function () {
	var that = this,
			pi = $.Deferred(),
			d = that.data,
			pis = [],
			atts = that.Att(null);

	//Prepare session urls
	if (!Object.size(atts)) pi.resolve();
	else {

		Object.keys(that.To.files(d))
		.forEach(function(s){
			if (atts[s]) {
				var u = that.Att(s, 'url');
				if (typeof u == 'string') that.Url[s] = u;
				else {
					u.then(function(res){ that.Url[s] = res; });
					pis.push(u);
				}
			}
		});

		if (!pis.length) pi.resolve();
		else {
			$.when.apply(null, pis).done(function(){ pi.resolve(); }.debounce(1));
		}
	}

	return pi.promise();
}

