com.data:
({
	id:"in.Cards",
	name:"Cards",
	params:{restyle:100},
	meta:{},

	M:{ /* Modes, see tab */ },
	RT:function(){return this;},
	Url:{},

	// Default data

	data:{
		type:'inliner-cards',
		mode:['normal'],

		title:'',
		layout:['uniform'],
		align:['center'],
		animate:'zoom',

		width:'250',
		gap:'10',
		bg:'#ffffff',
		border:'#efefef',
		ratio:'4:3',
		bullet:{
			char:'—',
			color:'#bbb'
		},
		text:{
			pad:'20px'
		},
		items:[]
	},

	// Init app

	init: function ($o, form) {
		var that = this,
				d = form.data,
				lang = 'en';

		// L10N

		if (cw.me && that.Lang[cw.me('locale')]) lang = cw.me('locale');
		that.Lang = that.Lang[lang];

		// HTML
		$o.html(that.HTML.join(''));

		// Make list
		that.MakeList();

		$o.then(function(){
			2..times(that.Reheight.lazy(400));
		}.debounce(100));


		// Load attachments
		if (that.Att) return that.GetAtts();

	},

	ui:{ /* See tab */ },

	HTML:[
		'<div class="in-w-btn-box">',
			'<button id="btn-gopts" class="hide"><span class="fi-wrench"></span></button>',
		'</div>',
		'<div id="oltitle"></div>',
		'<div class="in-w-cards-frame">',
			'<div class="in-w-cards-box">',
				'<div id="vc-cards" class="in-w-cards"></div>',
			'</div>',
		'</div>',
		'<div id="vc-empty">',
			'Empty',
		'</div>'
	]
})

({

	'#oltitle':{
		bind:	function(d){
			var tag = /^(large|normal)$/.test(d.mode+'')?'h2':'h3';
			return d.title?'<'+tag+'>'+d.title+'</'+tag+'>':'';
		},
		watch:'#vc-cards',
		css:{
			hide:function(d){return !d.title;}
		}
	},
	'#vc-cards':{
		delay:10,
		bind:'this.List',
		//merge:false,
		//check:true,
		manifest:function(elt){
			var m = this.data.mode+'';
			return this.M[m] || this.M.normal;
		},
		//id:['id'],
		//hash:function(e){return cw.lib.sdbm(e)},
		list:'<div class="wcard in-w-cards-item vc-cover"></div>'
	},
	'#vc-empty':{
		delay:50,
		bind: function(){
			var that = this,
					ctr = 0,
					l,
					$c;
			if (that.List.length) {
				$c = that.my.find('#vc-cards>.wcard');
				l = $c.size();
				(l+1).times(function(){
					$c.eq(ctr).removeClass('vc-cover');
					if (ctr == l) that.Reheight.debounce(150)();
					ctr+=1;
				}.lazy(l>20?15:30));
			}
			return that.Lang.EMPTY;
		},
		css:{
			hide:function(){return !!this.List.length;}
		},
		watch:'#vc-cards'
	},
	"#btn-gopts":{
		bind: function(d,v,$o) {
			var that = this;
			if(v!=null) {
				if ($o.data('modal')) $o.modal(true);
				else {
					var root = $o.parents('#medium');
					$o.modal({
						root:root.size()?root:undefined,
						bound:0,
						width:675,
						manifest:$.extend(true, {}, that.Opts, {Att:that.Att}),
						data:Object.clone(d, true),
						align:'right:38px;',
						screen:'rgba(255,255,255,0.65)',
						css:'p15',
						nose:'right',
						close:false,
						focus:false,
						enter:true,
						esc:true
					})
					.then(function(res){
						$.extend(d, Object.reject(res,'items'));
						d.items = res.items;
						that.MakeList();
						that.GetAtts().then(function(){
								that.my.recalc('#vc-cards');
								that.my.root().my('restyle');
								that.Reheight.debounce(150)();
						});

					}.debounce(50));
				}
			}
		},
		css:{
			'self:hide':function(){return !!this._compiled}
		}
	}

})

