({
	id:'in.Cards.Normal',
	inherit:['RT'],
	data:{
		title:'',
		photo:'',
		url:'',
		desc:'',
		list:[],
		link:{
			url:'',
			text:'',
			mode:[],
			color:''
		},
		banner:{
			text:'',
			color:''
		},
		bg:'',
		logo:'',
		scale:'100'
	},
	init:[
		'<div id="vc-banner" style="position:absolute"></div>',
		'<div id="vc-photo"></div>',
		'<div id="vc-text">',
		'<h3 id="vc-title"></h3>',
		'<div id="vc-desc"></div>',
		'<ul id="vc-list"></ul>',
		'<div id="vc-link"></div>',
		'</div>'
	],
	ui:{
		'#vc-banner':{
			bind:'banner.text',
			css:{
				hide: function(d){ return !(d.banner.text)}
			}
		},
		'#vc-photo':{
			bind:function(d,v,$o){
				var that = this;
				$o.css({
					'background-image':'url('+(that.RT().Url[d.photo]||d.photo)+')'
				});

				if (d.link.mode.indexOf('image') == -1) return '';

				return '<a href="'+d.link.url+'" ' + that.Tgt() + '> </a>';
			},
			css:{
				hide: function(d){ return !(d.photo+'')}
			}
		},
		'#vc-desc':{
			bind: function(d,v){
				var that = this;
				if (!d.list.length && that.RT().data.align == "center") return '<center>'+d.desc+'</center>';
				return d.desc;
			},
			css:{
				hide: function(d){ return !(d.desc)}
			}
		},
		'#vc-list':{
			bind: function(d){
				return (d.list || []).map(function(e){
					return '<li>'+e+'</li>';
				}).join('');
			},
			css:{
				hide: function(d){return !(d.list || []).length}
			}
		},
		'#vc-title':{
			bind:function(d){
				if (d.link.mode.indexOf('header') != -1){
					return '<a href="'+d.url+'" ' + this.Tgt() + '>'+d.title+'</a>';
				} 
				else return d.title;
			},
			css:{
				hide: function(d){ return !(d.title)}
			}
		},
		'#vc-link': {
			bind:function(d){
				if (d.link && d.link.url && d.link.text) {
					return '<a href="'+d.link.url+'" target="_blank">'+d.link.text+'</a>';
				}
			},
			init: function($o){
				var d = this.data,
						link = d.link,
						m  = d.link.mode;
				if (link) {
					if (m.indexOf('link') != -1) $o.addClass('vc-link');
					if (m.indexOf('button') != -1) $o.addClass('vc-button');
				}
			},
			css:{
				hide: function(d){ return !(d.link && d.link.url && d.link.text)}
			}
		}
	},
	style:{
		'': function(){
			var scale = +this.data.scale || 100,
					css = 'transform:scale('+scale/100+');';
			if (scale >100 ) css+='z-index:'+(scale-100 | 0)+';'
			return css;
		},
		' #vc-text':{
			' ': function(){
				var d = this.RT().data,
						item = this.data,
						align = 'text-align:'+(item.align || d.align)+';',
						bcolor = (item.link?item.link.color:'')||d.border,
						bradius = (item.link?item.link.corner:'')||this.RT().meta.theme.corner.outer;
				return {
					'':'line-height: 1.4em;font-size: 1em;padding:'+d.text.pad+';display:block;',
					' #vc-title':'display:inline-block;vertical-align:top;width:100%;'+align,
					' #vc-link':{
						'':'margin:1em 1.2em 0 0;font-size:1.1em;'+align,
						'.vc-button a':{
							'':'text-decoration:none!important; border:1px solid transparent!important;padding:0.2em 0.6em 0.4em 0.6em;'
							+' background-color:'+bcolor+';color:white!important;transition: color 0.3s, border 0.3s, background-color 0.3s;'
							+'border-radius:'+bradius+';line-height:1.2em;display:inline-block;min-width:130px;text-align:center;',
							':hover':'background:white;border:1px solid '+bcolor+'!important;color:'+bcolor+'!important;'
						}
					}
				}
			},
		},
		' #vc-photo a':'position:absolute; width:100%; height:100%; top:0;left:0;border-bottom:none!important;'
	},
	Tgt: function(){
		var that = this,
				d = that.data,
				link = d.link.url,
				tgt = ' target="_blank"';

		if (
			link 
			&& /^http/.test(link) 
			&& link.split("/")[2] == window.location.hostname
		) tgt = '';

		return tgt;
	}
})

(function(){
	var that = this;
	// Realign heights

	if (that.List.length) {
		var $c = that.my.find('#vc-cards>div'),
				h = 0,
				hs = {},
				rows = [];
		$c.each(function(i,e){
			var hc = $(e).outerHeight(),
					tc = $(e).offset().top;
			if (!hs[tc|0] || hs[tc|0]<hc) hs[tc|0] = hc;
			rows[i] = tc | 0;
		});
		$c.each(function(i,e){
			$(e).css({height:hs[rows[i]]+'px'});
		});
	}

})

({
	data:function(item, options){
		var that = this,
				d = item,
				h = '',
				id = Number.random(1e6, 1e7-1),
				opts = Object.merge(
					{path:''}, 
					Object.isObject(options)?options:{}						
				);
		var obj, s = item.data.replace(/^\(|\)$/g,'');

		try {
			obj = JSON.parse(s);
		} catch(e){}

		if (!Object.isObject(obj)) return item.data;

		if (opts.path || opts.Urls) {

			// Fix global params

			// Fix items
			obj.items = obj.items.map(function(e){

				['logo','photo','bg'].forEach(function(key){
					var s = e[key]+'';
					if (!s || s.to(1) == '#' || s.to(1)=='/' || /^http[s]?:/.test(s)) return;
					e[key] = opts.Urls?opts.Urls[s]:(opts.path+s);
				});
				return e;
			});
		}

		return '('+JSON.stringify(obj)+')';
	},
	files: function(data){
		var list = {};
		data.items.forEach(function(e){
			['logo','photo','bg'].forEach(function(key){
				var s = e[key]+'';
				if (s.to(1) == '#' || s.to(1)=='/' || /^http[s]?:/.test(s+'')) return;
				list[s] = true;
			});
		});
		return list;
	}
})

({

	' #vc-empty':'opacity:0.7;padding: 10px 0;text-align:center;',

	' .in-w-cards-frame':'width:100%;overflow:hidden;', 

	' .in-w-cards-item':'transition:height 0.1s;text-align:left;',

	'':{
		' .wcard':'display:inline-block; vertical-align:top;overflow:hidden;',
		' ': function($o){
			var d = this.data,
					meta = this.meta,
					w0 = $o.width(),
					wcard = (+d.width<w0?+d.width:w0),
					w = ((+d.gap+w0)/Math.round(w0/wcard-0.45)|0)-(d.gap|0),
					g1 = (d.gap/2|0)+'px ',
					g0 = 2*(d.gap/2|0)+'px ',
					g25 = (d.gap/2.5|0)+'px ',
					align = d.align+'',
					ratio = d.ratio.split(/\D/),
					fheight = w/ratio[0]*ratio[1]|0,
					animate = {
						zoom:'transform:scale3d(0.3,0.3,1);',
						flip:'transform:scale3d(0.3,0.2,0.1) rotate3d(1, 0,0.1, 30deg) translateY(-150%)',
						drop:'transform:scale3d(0.3,0.2,0.1) translateY(-150%)',
						fade:' '
					},
					transStart;

			if (!meta._mobile) {
				transStart = animate[d.animate] || '';
				if (transStart) transStart = 'opacity:0.05;'+transStart;
			}

			return {
				' .in-w-cards-item':'border:1px solid '+d.border+';' +(d.bg?'background:'+d.bg+';':'white;'),

				' .in-w-cards-box':'width:'+(+d.gap+w0)+'px;margin:0 -'+g1+'-'+g1+'-'+g1+';',
				' .in-w-cards, #vc-empty ':'text-align:'+align+';',
				' .wcard':{
					'':'width:'+w+'px;margin:0 '+g1 + g0 + g1 +';',
					' ':'opacity:1; transition:opacity 0.1s, transform 0.3s, transform-origin 0.1s;'
						+'transform-origin:50% '+fheight+'px 0;',
					'.vc-cover':transStart,
					' #vc-photo':'width:100%;height:'+fheight+'px;background-size:cover;background-position:50% 30%;',
					' h4, h3':'margin:0 0 0.5em 0!important;',
					' p':'margin:0em 0 0em 0!important;line-height:1.45em!important;',
					' li':'list-style-type:none;margin:0.4em 0!important;line-height:1.3em!important;',
					' ul':'padding-left:1.2em!important;',
					' li:before':'content:"'+d.bullet.char+'";margin-left:-1.2em;display:inline-block;width:1.2em; color:'+d.bullet.color+';'
				},
				' #oltitle':{
					'':'margin-bottom:'+meta.theme.pad.gap+';padding:0 0;',
					' h3':'text-align:'+align+';margin:0!important;',
					' h2':'text-align:'+align+';margin:0!important;'
				}
			};
		}
	},

	// Opts button
	' .in-w-btn-box':'width:100%;z-index:+10;',

	' #btn-gopts':'position: absolute; top: 5px; right:5px; font-size:17px;z-index:+3;border-radius:100px!important;'
			+'padding:0; line-height:30px;width:30px;height:30px;margin:0;opacity:0.5;',

	' #btn-gopts:hover':'opacity:1;',
})

(function () {
	var that = this,
			d = that.data,
			items = d.items,
			item = {
				bg:d.bg,
				padding:d.gap,
				border:d.border,
				align:d.align+'',
				scale:100,
				ratio:'16:9',
				padPreview:false
			};
	if (!items.length) {
		that.List = []; 
		return;
	}

	that.List = items.map(function(e){ return $.extend(true, {}, item, e); });

})

(function(){
	var that = this,
			pi = $.Deferred(),
			d = that.data,
			pis = [],
			atts = that.Att(null);

	//Prepare session urls
	if (!Object.size(atts)) pi.resolve();
	else {

		Object.keys(that.To.files(d))
		.forEach(function(s){
			if (atts[s]) {
				var u = that.Att(s, 'url');
				if (typeof u == 'string') that.Url[s] = u;
				else {
					u.then(function(res){ that.Url[s] = res; });
					pis.push(u);
				}
			}
		});

		if (!pis.length) pi.resolve();
		else {
			$.when.apply(null, pis).done(function(){ pi.resolve(); }.debounce(1));
		}
	}

	return pi.promise();
})

