({
	"#master": {
		recalcDepth:1,
		bind: "items",
		list: '<div class="mitem"><span id="name" class="pseudolink tdn ml3"></span></div>',
		init: function ($list, form){
			var that = this;
			// Explicitly process click on left col‘s elt
			$list.on("switch.my", function (event, item){
				that.my.find(".vc-active").removeClass("vc-active");
				that.Detail[0]=item;
				that.my.recalc('#gi');
				return false;
			})
			.sortable();
			//$list.niceScroll();
		},
		hash: ['title','desc','type'],
		id:['title','type','desc','photo','link'],
		manifest: {
			inherit:['Lang'],
			ui: {
				"#name": {
					bind: function (d, v){ 
						var that = this;
						if (null!=v) {
							that.my.trigger(null, "switch", d);
