manifest.GetAtts:
function () {
	var that = this,
			pi = $.Deferred(),
			d = that.data,
			pis = [],
			atts = that.Att(null);

	//Prepare session urls
	if (!d.items.length || !Object.size(atts)) pi.resolve();
	else {
		d.items.forEach(function(e){
			var s = e.url;
			if (atts[s]) {
				var u = that.Att(s, 'url');
				if (typeof u == 'string') that.Url[s] = u;
				else {
					u.then(function(res){ that.Url[s] = res; });
					pis.push(u);
				}
			}
		});
		if (!pis.length) pi.resolve();
		else {
			$.when.apply(null, pis).done(function(){ pi.resolve(); }.debounce(1));
		}
	}

	return pi.promise();
}

To.data:
function (item,options) {
		var that = this,
				d = item,
				h = '',
				id = Number.random(1e6, 1e7-1),
				opts = Object.merge(
					{path:''}, 
					Object.isObject(options)?options:{}						
				);
		var obj, s = item.data.replace(/^\(|\)$/g,'');

		try {
			obj = JSON.parse(s);
		} catch(e){}

		if (!Object.isObject(obj)) return item.data;

		if (opts.path || opts.Urls) {
			obj.items = obj.items.map(function(e){
				var s = e.url;
				if ((s+'').to(1)=='/' || /^http[s]:/.test(s+'')) return e;
				e.url = opts.Urls?opts.Urls[s]:(opts.path+s);
				return e;
			});
		}

		return '('+JSON.stringify(obj)+')';
	}

To.files:
