style..in-w-gallery:
function ($o) {

		var that = this,
				d = that.data,
				n = d.items.length,
				gap = d.gap/2,
				w0 = 99.999,
				ww = $o.width(),
				scheme = ww<500?'narrow':ww<1000?'normal':'wide';

		that.Layout = scheme;

		var t = that.L[scheme][n],
				k = d.ratio.split(":")[1]/d.ratio.split(":")[0],
				x = (w0/t.w).round(4),
				y = (k*(ww+gap*2)/t.w).round(4),
				vc = 'calc(100% + '+d.gap+'px);';

		var res = {
			' ':'width:'+vc+'margin:-'+gap+'px;'+(scheme=='wide'?'white-space: nowrap;':''),
			' .in-w-gallery-col':'display:inline-block;vertical-align:top;white-space: normal;',
			' .in-w-gallery-cell':'display:inline-block;vertical-align:top;padding:'+gap+'px;overflow:hidden;',
			' .in-w-gallery-item':'width:100%; display:block;background-size:cover;background-repeat:no-repeat;'
			+'background-position:center center; background-color:'+d.bg+';'
			+(d.action=='zoom'?'cursor:zoom-in;':'')
		};

		var cells = {}, its = {};
		if (t.c) {
			t.c.forEach(function(a, idx){
				var col = a[0];
				cells[col]=1;

				a.from(1).forEach(function(i){ its[idx+'-'+i] = {i:i, w:100*i/col}; })
			});

			Object.keys(cells).forEach(function(i){
				var wr = (x*i).round(4);
				res[' .in-w-gallery-c'+i] = 'width:'+wr+'%;';

			});

			Object.keys(its).forEach(function(i){
				var wc = its[i].w.round(4);
				res[' .in-w-gallery-i-'+i] = 'width:'+wc+'%!important;'
				res[' .in-w-gallery-z-'+i] = 'height:'+(y*its[i].i-gap*2).round(4)+'px!important;';
			});
		}

		(function($f){$f.find('#cards').trigger('recalc');}).fill($o).delay(5);

		return res;
	}

