Fullscreen.Align:
function (x) {
		var that = this,
				d = that.data,
				idx = d.idx,
				shifted = false;

		if (idx == 0 && x<0) {
			that.my.find('#pcont')
			.removeClass('ptrans')
			.css({
				transform:'translateX('+(-d.w * (d.items.length+1))+'px)'
			});
			shifted = true;
		}
		else if (idx == d.items.length-1 && x>0) {
			that.my.find('#pcont')
			.removeClass('ptrans')
			.css({
				transform:'translateX(0px)'
			});
			shifted = true;
		}

		(function() {
			if (shifted) that.my.find('#pcont').addClass('ptrans');
			d.idx = (d.idx + d.items.length + (+x)) % d.items.length;
			that.my.find('#pcont').css({
				transform:'translateX('+(-d.w * (d.idx+1))+'px)'
			});
		}).delay(20);
	}

