	},
	Url:{},
	ui:{
		'#cards':{
			init: function($o, form){
				var that = this;
				// Zoom
				if (that.data.action+'' == 'zoom') {
					$o.on('click.my', '.in-w-gallery-item', function(evt){
						// if (that.data.items.length<2) return false;
						var idx = $(this).data('idx'),
								d = that.data;

						// Prepare modal, calculate width
						var ww = $(window).width() - 40,
								wh = $(window).height() -100,
								ar = d.ratio.split(/\D/),
								ratio = ar[0]/ar[1],
								width = (Math.min(ww/ratio, wh).clamp(100,900) * ratio | 0).clamp(200,1600),
								height = width/ratio | 0;

						$.my.modal({
							manifest:$.extend({Url:that.Url},that.Fullscreen),
							width:width,
							data:{
								w:width,
								h:height,
								items:d.items,
								idx:idx,
							},
							esc:true,
							close:false,
							enter:true,
							css:'p10'
						})
					});
				}
				if (that.data.action+'' == 'link') {

					$o.on('click.my', '.in-w-gallery-item', function(evt){
						if (that.data.items.length<2) return false;
						var idx = $(this).data('idx'),
								row = that.data.item[idx],
								link = row.link || '';

						if (link) window.location.href=link;
					});

				}
			},
			bind: function(d){
				var that = this,
						t = that.L[that.Layout][d.items.length],
						imgs = Object.clone(d.items, true),
						h = '',
						ctr = 0;

				if (imgs.length) {

					t.c.forEach(function(col, idx){
						var a = col.slice(1);
						h+='<div class="in-w-gallery-col in-w-gallery-c'+col[0]+'">';

						a.forEach(function(cell){
							var img = imgs.shift();
							if (img) {
								h+=[
									'<div class="in-w-gallery-cell in-w-gallery-i-'+idx+'-'+cell+'">',
										'<div class="in-w-gallery-item in-w-gallery-z-'+idx+'-'+cell+'" ',
										'data-idx="'+ctr+'" ',
										'style="background-image:url(\''+(that.Url[img.url]||img.url)+'\');">',
											(img.desc?'<div class="in-w-gallery-item-desc">'+img.desc+'</div>':''),
										'</div>',
									'</div>'
								].join('');

								ctr+=1;

							}
						});

						h+='</div>';
					});
				}

				return h;
			}
		},
		'#nocards':{
			bind:'this.Lang.EMPTY',
