			}
		},
		"#btn-gopts":{
			bind: function(d,v,$o) {
				var that = this;
				if(v!=null) {
					if ($o.data('modal')) $o.modal(true);
					else {
						var root = $o.parents('#medium');
						$o.modal({
							root:root.size()?root:undefined,
							bound:0,
							width:705,
							manifest:$.extend(true, {}, that.Opts, {Att:that.Att}),
							data:Object.clone(d, true),
							align:'right:38px;',
							screen:'rgba(255,255,255,0.65)',
							nose:'right',
							focus:false,
							enter:true,
							esc:true
						})
						.then(function(res){

							['items','gap','bg','ratio','action','round']
							.forEach(function(k){d[k]=res[k]});

							that.GetAtts().then(function(){
								that.my.check("#cards");
								that.my.root().my('restyle');
								that.my.root().trigger("change");
							})
						});
					}
				}
