		+'background-image: -webkit-linear-gradient(top, rgba(0,0,0,0), rgba(0,0,0,0.4), rgba(0,0,0,0.5), rgba(0,0,0,0.50));'
		+'background-image: -o-linear-gradient(top, rgba(0,0,0,0), rgba(0,0,0,0.4), rgba(0,0,0,0.5), rgba(0,0,0,0.50));'
		+'background-image: -ms-linear-gradient(top, rgba(0,0,0,0), rgba(0,0,0,0.4), rgba(0,0,0,0.5), rgba(0,0,0,0.50));'
		+'background-image: linear-gradient(top, rgba(0,0,0,0), rgba(0,0,0,0.4), rgba(0,0,0,0.5), rgba(0,0,0,0.50))',

	' .in-w-gallery-item:hover .in-w-gallery-item-desc':'opacity:1;',

	' .in-w-gallery-box ':function($o){
		var r = this.data.round || '0';
		if (r == '0') return '';
		r = r+'px;'
		return {
			'':'border-radius:'+r,
			'#cards>div:last-child>div:last-child>div':'border-bottom-right-radius:'+r
		};
	},

	' .in-w-gallery':function($o){

		var that = this,
				d = that.data,
				n = d.items.length,
				gap = d.gap/2,
				w0 = 99.999,
				ww = $o.width(),
				scheme = ww<500?'narrow':ww<1000?'normal':'wide';

		that.Layout = scheme;

		var t = that.L[scheme][n],
				k = d.ratio.split(":")[1]/d.ratio.split(":")[0],
				x = (w0/t.w).round(4),
				y = (k*(ww+gap*2)/t.w).round(4),
				vc = 'calc(100% + '+d.gap+'px);';

		var res = {
			' ':'width:'+vc+'margin:-'+gap+'px;'+(scheme=='wide'?'white-space: nowrap;':''),
			' .in-w-gallery-col':'display:inline-block;vertical-align:top;white-space: normal;',
			' .in-w-gallery-cell':'display:inline-block;vertical-align:top;padding:'+gap+'px;overflow:hidden;',
			' .in-w-gallery-item':'width:100%; display:block;background-size:cover;background-repeat:no-repeat;'
			+'background-position:center center; background-color:'+d.bg+';'
			+(d.action=='zoom'?'cursor:zoom-in;':'')
		};

		var cells = {}, its = {};
		if (t.c) {
			t.c.forEach(function(a, idx){
				var col = a[0];
				cells[col]=1;

				a.from(1).forEach(function(i){ its[idx+'-'+i] = {i:i, w:100*i/col}; })
			});

			Object.keys(cells).forEach(function(i){
				var wr = (x*i).round(4);
				res[' .in-w-gallery-c'+i] = 'width:'+wr+'%;';

			});

			Object.keys(its).forEach(function(i){
				var wc = its[i].w.round(4);
				res[' .in-w-gallery-i-'+i] = 'width:'+wc+'%!important;'
				res[' .in-w-gallery-z-'+i] = 'height:'+(y*its[i].i-gap*2).round(4)+'px!important;';
			});
		}

		(function($f){$f.find('#cards').trigger('recalc');}).fill($o).delay(5);

		return res;
	}
})

(function(){
	var that = this,
			pi = $.Deferred(),
			d = that.data,
			pis = [],
			atts = that.Att(null);

	//Prepare session urls
	if (!d.items.length || !Object.size(atts)) pi.resolve();
	else {
		d.items.forEach(function(e){
			var s = e.url+'';
			if (atts[s]) {
				var u = that.Att(s, 'url');
				if (typeof u == 'string') that.Url[s] = u;
				else {
					u.then(function(res){ that.Url[s] = res; });
					pis.push(u);
				}
			}
		});
		if (!pis.length) pi.resolve();
		else {
			$.when.apply(null, pis).done(function(){ pi.resolve(); }.debounce(1));
		}
	}

	return pi.promise();
})

({
	data:function(item, options){
		var that = this,
				d = item,
				h = '',
				id = Number.random(1e6, 1e7-1),
				opts = Object.merge(
					{path:''}, 
					Object.isObject(options)?options:{}						
				);
		var obj, s = item.data.replace(/^\(|\)$/g,'');

		try {
			obj = JSON.parse(s);
		} catch(e){}

		if (!Object.isObject(obj)) return item.data;

		if (opts.path || opts.Urls) {
			obj.items = obj.items.map(function(e){
				var s = e.url;
				if ((s+'').to(1)=='/' || /^http[s]:/.test(s+'')) return e;
				e.url = opts.Urls?opts.Urls[s]:(opts.path+s);
				return e;
			});
		}

		return '('+JSON.stringify(obj)+')';
	},
	files: function(data){
		var list = {};
		data.items.forEach(function(e){
			if (!/^http[s]:\/\//.test(e.url)) list[e.url] = true;
		});
		return list;
	},
  updateFiles: function(data, replacer){
  	var list = {};
		data.items.forEach(function(e){
			if (!/^http[s]:\/\//.test(e.url)) list[e.url] = true;
		});
		if (!Object.isObject(replacer)) return data;

    data.items.forEach(function(e){
			if (!list[e.url]) return;
      if (replacer[e.url]) e.url = replacer[e.url];
		});
    return data;
  }
})

