#ilist.bind:
function (d,v,$o) {
		var that = this, 
				i, e, html="",
				list = d.doc.items, 
				res = [], fl = "";

		// sort list
		list = list.sortBy(function(e){
			return [
				(!e.folder) *1,
				e.folder,
				e.snippet*1,
				e.name,e.type
			].join("/").toLowerCase();
		});

		// gen items/folders final list
		for (i=0; i<list.length; i++) {
			e = list[i];
			if (e.folder && e.folder != fl) {
				fl = e.folder;
				res.push({
					id:fl,
					html: '<span class="dib w15">'+(that.Expanded[fl]?"▾":"▸")+'</span>'+fl,
					css:"xgray",
					type:"folder"
				});
			} 
			if (!e.folder || that.Expanded[e.folder]) {
				var isdirty = (!e.state.savedSdbm || e.state.sdbm == "Z"  || (e.state.sdbm!==e.state.savedSdbm));
				res.push({
					id:e.id,
					html: (e.folder?"":"&nbsp;")
					+ (e.parserActive?'<i>':"")
					+ e.name+"."+e.ext
					+ (e.parserActive?'</i>':"")
					+ (isdirty?'<span class="lgray">&nbsp;•</span>':""),
					css: (e.external?"orange o80":e.snippet?"sky":e.type==="attachment"?"gray":"blue") + (e.id == that.Active?" bg-lblue":""),
					type:e.snippet?"snippet":"item",
					title: e.folder+"/"+e.name+"."+e.ext,
					folder: e.folder
				});
			}
		}

		// render list
		for (i=0;i<res.length;i++) {
			e = res[i];
			html+='<div class="siteitem w190 oh '+e.css+(e.folder?" pl15":"")
				+'" id="sx-'+e.id+'" title="'
				+(e.title?e.title:"")+'">'
				+e.html
				+'</div>';
		}
		return html;
	}

