				silent:true
			}, $o)
			.then(
				function(res){
					var i, 
						T = that.Tar.Tar(),
						tar = new T(), 
						a = res.result._attachments,
						url,r;

					if (Object.size(a)) {

						for (i in a) {
							r = tar.append(i, that.Tar.Bytes(a[i].data));
						}

						try {
							url = URL.createObjectURL(new Blob([r], {type : 'application/x-tar'}));
						} catch(e) {
							console.log(e, e.stack);
						}
						if (url) {
							$o.modal({
								manifest:{
									init:[
