#file.bind:
function (d,v,$o) {

	    		var f,that = this,
					$r = $o.my().root,
					$img = $r.find("#source"),
					img="";


				if (v!=null && v) {
					f = $o[0].files[0];
					if (f) (function(){
						d.filename = f.name
							.toLowerCase()
							.replace(/[^a-z0-9\-\.]/gi,'')
							.replace(/\.[^\.]+$/,"")+".jpg";

						var fr = new FileReader(), ri, sb64 = [];
						fr.onload=function(e){
							ri = new Uint8Array(e.target.result);
							for (var i=0; i<ri.length; i++) sb64.push(String.fromCharCode(ri[i]));
							img = window.btoa(sb64.join(""));
						};

						fr.onloadend = function (){
							$img.load(function(){
								d.scale = $img[0].naturalWidth/$img.width();
								$img.Jcrop({
									onChange: preview,
									onSelect: preview.debounce(20),
									allowMove:true
								}, function (){
									this.animateTo([0,0,100,100]);
									d.crop.z=1;
									$o.my("find","#folder").trigger("check");
								});
								img="";
								$o.parent().addClass("hide");
							});
							$img.removeClass("hide")
								.attr("src","data:image/jpeg;base64,"+img);

							//d.scale=$img[0].naturalWidth/$img.width();	
						};
						fr.readAsArrayBuffer(f);	
					})();
				}

				function preview(c){
					$.extend(d.crop, Object.select(c,["x","y","w","h"]));
					d.cropped=true;

					that.Recalc(d, $o, true);
				}
			}

#btn-apply.bind:
function (d,v,$o) {
				var f, that = this,
					$r = $o.my().root,
					$c = $r.find("#x1"),
					$img = $r.find("#source"),
					$c2 = $r.find("#x2"),
					n={k:d.scale}, img="",
					width=d.width*1.5;

				if (v!=null && d.cropped) {
					_create (d.crop);

					d.data = $o.my("find","#x1")[0]
						.toDataURL('image/jpeg',0.97)
						.substr(23);
					$o.my().root.trigger("commit");
				}

				function _create (c){
					if(parseInt(c.w) > 0) {
						var img = $img[0], cs, ctx, cs2, ctx2, 
							k = n.k,
							w = c.w*k, // scale width/height
							h = c.h*k; 

						if (!that.Active) {
							cs = $c[0];
							cs.width=d.width;
							cs.height=d.height;
							ctx = cs.getContext("2d");
							ctx.drawImage(img, (c.x*k).floor(), (c.y*k).floor(), d.width*1, d.height*1, 0, 0, d.width*1, d.height*1);

						} else { 
							if (w>d.width*2) {
								cs2 = $c2[0];
								cs2.width=d.width*2;
								cs2.height=2*d.height;
								ctx2 = cs2.getContext("2d");
								ctx2.drawImage(img, c.x*k, c.y*k, w, h, 0, 0, d.width*2,d.height*2);

								cs = $c[0];
								cs.width=d.width;
								cs.height=d.height;
								ctx = cs.getContext("2d");
								ctx.drawImage(cs2, 0, 0, d.width*2,d.height*2, 0, 0, d.width*1, d.height*1);
							} else {
								cs = $c[0];
								cs.width=d.width;
								cs.height=d.height;
								ctx = cs.getContext("2d");
								ctx.drawImage(img, c.x*k, c.y*k, w, h, 0, 0, d.width*1, d.height*1);
							}
						}
					}
				}
			}

DocParams.init:
function ($o,form) { 
		$o.formgen(this.HTML);
		var that = this, d = form.data;
		if (!(d.creds.crypto+"")) {
			$.extend(!0, that.s3, d.s3);
		} else if (cw.crypto.has(d.creds.crypto)) {
			if (!d.creds.CRYPTO) {
				d.creds.crypto = "";
			}
			else {
				var s3 = cw.crypto.dec(d.creds);
				if (!s3.CRYPTO) {
					$.extend(!0, that.s3, s3.s3);
				}
				else {
					that.nokey = true;
				}
			}

		} else {
			that.nokey = true;
		}
	}

