manifest.Build:
function (doc0,opts0,$o) {

	var i,j,that = this,
			d = that.data,
			pi = $.Deferred(),
			tdoc = doc0 || that.data.doc,

			opts = $.extend(!0,{ 
				buttons:true, 
				footer:"", 
				checkcreds:false,
				pick:"",
				resolve:true,
				silent:false,
				blocking:true
			},Object.reject(opts0||{},"errors")),

			errors = (opts0||{}).errors || {},
			key,
			items={};

	for (i=0;i<tdoc.items.length;i++) {
		if ( !tdoc.items[i].snippet && (j=that.Path(tdoc.items[i])).to(1) != ".") {
			items[j.from(1)] = tdoc.items[i];
		}
	}


	if (key = d.doc.creds.crypto) {
		if (!cw.crypto.has(key)) {
			// cw.lib.note("No cryptokey to decode CouchDB credentials.", "error");
			if (opts.checkcreds) pi.reject("Can’t get S3 credentials.");
			that.creds.s3 = {region:""}
		} else {
			var s3 = cw.crypto.dec(d.doc.creds)["s3"];
			if (s3) {
				that.creds.s3 = s3;
			} else {
				cw.lib.note("Error decoding S3 credentials.", "error");
				if (opts.checkcreds) pi.reject("Can’t get S3 credentials.");
				that.creds.s3 = {region:""}
			}
		}
	} else {
		that.creds.s3 = Object.clone(d.doc.s3, !0);
	}

	try {
		if (opts.resolve) that.ResolveExternals(tdoc, errors).always(_process);
		else _process();

	} catch(e) {
		pi.reject("Error during validation", e.message, e.stack);
	}

	return pi.promise();


	//- - - - - - - - - - - - - - -

	function _process (){
			var a = that.Items2Atts( tdoc,  that.Types,  errors,  opts.pick?opts.pick:true);

			//a.result._id = that.creds.s3.region;

			if (Object.size(a.err)) {
				$o.modal({
					manifest:that.ParseErrors,
					data:$.extend(!0, {}, opts,a ),
					esc:true,
					enter:true,
					screen:opts.blocking?'rgba(255,255,255,0.5)':false,
					align:"top:105%",
					nose:"top",
					bound:10,
					drag:!opts.blocking
				})
				.then(function(){
					_result(a);
				})
				.fail(function(){
					pi.reject("Cancelled");
				});
			} 
			else {
				if (!opts.silent) cw.lib.note("Doc integrity seems to be Ok.");
				_result(a);
			}
		}


	//- - - - - - - - - - - - - - -

	function _result(a){
		if (a.result) {
			var att  = a.result._attachments;

			// Make it last node of the doc
			delete a.result._attachments;
			a.result._attachments = att;

			//console.log(a);

			// load binary atts
			that.db.get(d.doc._id, {
				attachments:true
			})
			.then(
				function(res){
					if (res._attachments) for (var i in res._attachments) {
						if (!a.result._attachments[i] || null == a.result._attachments[i].data) {
							a.result._attachments[i] = {
								content_type:res._attachments[i].content_type,
								data:res._attachments[i].data
							}
						}
						if(items[i]) items[i].state.crc = $.my.f.sdbmCode(res._attachments[i].data);
					};
					pi.resolve(a);
				},
				function(){
					if (!opts.silent) cw.lib.note("Doc is not yet saved, you may need to save doc.", "warning");
					pi.resolve(a);
				}
			);
		}

	} // --end _result

}

manifest.ResolveExternals:
function (doc0,errs) {
	var that = this, 
			pis,
			async,
			pending = {},
			rows = (doc0 || that.data.doc).items,
			z = $.Deferred(),
			hasErrors = false,
			err = errs || {};

	pis = rows.map(function(e,i){
		var s="", id='', 
				p = e.external, 
				pi;
		if (p && !/^[x\s]/.test(p)) {
			async = true;
			pi = $.Deferred();

			// Local doc
			if (p.startsWith("pouch://") || p.startsWith("local://")) {
				p = p.from(8).split(/\s+/).compact(true);
				s = p[1]||'';
				id = p[0];

				if (!pending["pouch "+id]) pending["pouch "+id] = that.db.get(id, {attachments:true});

				pending["pouch "+id].then(
					function(doc) {
						if (s) {
							var r = cw.lib.getref(doc, s);
							if (r) {
								pi.resolve(r);
							} else {
								pi.reject("Failed to process local doc '"+id+"', property '"+s+"' not found");
							}
						} else {
							if (doc) pi.resolve(doc);
							else pi.reject("Failed to load local doc '"+id+"'");
						}
					},
					function(){ pi.reject("Failed to load local doc '"+id+"'"); }
				);
			}

			// Local doc, other DB
			else if (p.startsWith("doc://")) {
				p = p.from(6).split(/\s+/).compact(true);

				s = p[1]||'';
				id = p[0];

				var dbid = id.split("/",1)[0];
				id = id.from(dbid.length+1);

				if (cw.db(dbid)) {

					if (!pending["doc "+p[0]]) {
						pending["doc "+p[0]] = cw.db(dbid).get(id, {attachments:true});
					}

					pending["doc "+p[0]].then(
						function(doc) {

							if (!doc) {
								pi.reject("Failed to load PouchDB doc '"+p[0]+"'");
							}

							else if (s) {
								var r = cw.lib.getref(doc, s);
								if (r) pi.resolve(r);
								else pi.reject("Failed to process PouchDB doc '"
															 +p[0]+"', property '"+s+"' not found");
							} else {
								pi.resolve(doc);
							}
						},
						function(){ pi.reject("Failed to load PouchDB doc '"+p[0]+"'"); }
					);
				} else {
					pi.reject("PouchDB DB '"+dbid+' does not exist' );
				}
			}

			// External request
			else if (/^http[s]?:\/\//.test(p)) {
				p = p.split(/\s+/).compact(true);
				s = p[1]||'';
				id = p[0];

				if (!pending["url "+id]) {

					if ('https:'==window.location.protocol && !id.startsWith(window.location.protocol)) {
						pending["url "+id] = $.Deferred().reject('forbidden protocol').promise();
					}
					else pending["url "+id] = $.ajax({
						url:id,
						cache:false,
						type:"GET",
						dataType:s?"json":"text",
						xhrFields:{withCredentials:/^https/.test(id)}
					});
				}

				pending["url "+id].then(
					function(doc) {
						var json, r;
						if (s) {
							if (!Object.isObject(doc) && !Object.isArray(doc)) {
								try { doc = JSON.parse(doc) } catch (e) { }
							}
							r = cw.lib.getref(doc, s);
							if (r) {
								pi.resolve(r);
							} else {
								pi.reject("Failed to process external doc '"+id+"', property '"+s+"' not found");
							}
						} else {
							if (doc) pi.resolve(doc);
							else pi.reject("Failed to load external doc '"+id+"'");
						}
					},
					function(){ pi.reject("Failed to load external doc '"+id+"'"); }
				);
			}

			// Unknown, reject
			else {
				pi.reject("Failed to load resource "+id+', protocol is unknown.');
			}

			return pi.promise();

		} else {
			return e.data;
		}
	});



	if (!async) z.resolve();
	else {
		Q.allSettled(pis)
		.then(
			function(reps){
				reps.forEach(function(e,i){

					var d = rows[i], aname = that.Path(d);

					if (e.state ==="fulfilled") {
						if (Object.isString(e.value)) {
							rows[i].data = e.value;
						}
						else {
							if (Object.isObject(e.value) && rows[i].ext=='js') {

								rows[i].data = cw.lib.js2txt(cw.lib.unjson(e.value),'\t');

							} else {
								rows[i].data = JSON.stringify(e.value,null,'\t');
							}
						}
					}
					else {
